--
-- Cấu trúc bảng cho bảng `daovanminh_post`
--

CREATE TABLE `daovanminh_post` (
  `postId` int(11) NOT NULL,
  `postTitle` varchar(100) CHARACTER SET utf8 NOT NULL,
  `alias` varchar(100) CHARACTER SET utf8 NOT NULL,
  `topicId` int(11) UNSIGNED NOT NULL,
  `image` varchar(100) CHARACTER SET utf8 NOT NULL,
  `summary` varchar(255) CHARACTER SET utf8 NOT NULL,
  `trash` tinyint(1) NOT NULL,
  `status` int(1) NOT NULL,
  `detail` text CHARACTER SET utf8 NOT NULL,
  `createBy` varchar(50) CHARACTER SET utf8 NOT NULL,
  `createDate` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  `updateDate` datetime DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `daovanminh_post`
--

INSERT INTO `daovanminh_post` (`postId`, `postTitle`, `alias`, `topicId`, `image`, `summary`, `trash`, `status`, `detail`, `createBy`, `createDate`, `updateDate`) VALUES
(1, '4 TÀI KHOẢN INSTAGRAM TRUYỀN CẢM HỨNG CHO NÀNG MÊ PHONG CÁCH COLOR-BLOCKING', 'instagram-color-blocking', 1, 'nina-sandbech-ban-phoi-thoi-trang-445x250.jpg', '<p>Bạn đang t&igrave;m nguồn cảm hứng cho những bản phối thời trang color-blocking s&agrave;nh điệu? H&atilde;y &ldquo;follow&rdquo; ngay 4 t&agrave;i khoản Instagram của c&aacute;c &ldquo;tay chơi&rdquo; m&agrave;u sắc thế giới với gu thẩm mỹ ri&ecirc;ng', 0, 1, '<p>Color-blocking l&agrave; c&aacute;ch kết hợp trang phục với những khối m&agrave;u sắc tương phản hoặc tương đồng dựa tr&ecirc;n nguy&ecirc;n tắc của b&aacute;nh xe m&agrave;u sắc. C&aacute;ch phối n&agrave;y vốn thịnh h&agrave;nh trong những năm 80 v&agrave; bắt đầu trở lại rầm rộ hơn bao giờ hết trong m&ugrave;a H&egrave; năm nay. Kh&ocirc;ng kh&oacute; để thấy c&aacute;c khối m&agrave;u tươi tắn, n&oacute;ng bỏng đang &ldquo;thống trị&rdquo; tr&ecirc;n khắp c&aacute;c t&agrave;i khoản&nbsp;<a href=\"https://www.elle.vn/tin-thoi-trang/tin-thoi-trang-nen-tang-instagram\">Instagram</a>&nbsp;của c&aacute;c t&iacute;n đồ thời trang quốc tế. Nếu đang ngần ngại trước xu hướng thời trang m&agrave;u sắc n&agrave;y, h&atilde;y theo d&otilde;i ngay t&agrave;i khoản Instagram của 4 &ldquo;ph&ugrave; thuỷ&rdquo; m&agrave;u sắc trong cộng đồng thời trang để c&oacute; cho m&igrave;nh cảm hứng l&ecirc;n đồ color-blocking thật thời thượng.&nbsp;</p>\r\n\r\n<h2>LEONIE HANNE &ndash; @LEONIEHANNE</h2>\r\n\r\n<p>Nhắc đến c&aacute;ch phối đồ color-blocking đầy m&agrave;u sắc, kh&ocirc;ng thể kh&ocirc;ng nghĩ tới c&ocirc; n&agrave;ng người mẫu &ndash; fashion&nbsp;<a href=\"https://www.elle.vn/the-gioi-thoi-trang/influencer-marketing-thuong-hieu-thoi-trang\">influencer</a>&nbsp;(người c&oacute; ảnh hưởng đến thời trang) Leonie Hanne với t&agrave;i khoản Instagram chạm ngưỡng 3,4 triệu lượt theo d&otilde;i. Phong c&aacute;ch &ldquo;chơi&rdquo; m&agrave;u tưởng như &ldquo;kh&oacute; nhằn&rdquo; n&agrave;y qua tay Leonie Hanne lại trở n&ecirc;n đầy dễ d&agrave;ng với h&agrave;ng loạt bản phối thời thượng.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Phối màu color blocking hồng neon và vàng canh\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/16/439139/color-blocking-hong-vang-neon-leoniehanne.jpg\" style=\"height:932px; width:750px\" /></p>\r\n\r\n<p>(Ảnh: @leoniehanne)</p>\r\n\r\n<p><img alt=\"phối màu color blocking\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/21/439139/phoi-mau-color-blocking-leonie-hanne.jpg\" style=\"height:1399px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @leoniehanne)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gh&eacute; thăm t&agrave;i khoản của Leonie Hanne, chắc chắn bạn sẽ kh&ocirc;ng khỏi cho&aacute;ng ngợp bởi những bản phối thời trang color-blocking s&agrave;nh điệu m&agrave; kh&ocirc;ng hề &ldquo;sến&rdquo; hay g&acirc;y kh&oacute; chịu cho người nh&igrave;n. Ngược lại, với gu thẩm mỹ tinh tế v&agrave; nhạy b&eacute;n với m&agrave;u sắc, c&ocirc; n&agrave;ng dễ d&agrave;ng &ldquo;chinh phục&rdquo; c&aacute;c bản phối thời trang với t&ocirc;ng m&agrave;u neon ch&oacute;i l&oacute;a như xanh l&aacute;, v&agrave;ng chanh hay&nbsp;<a href=\"https://www.elle.vn/xu-huong-phong-cach/mix-mau-quan-ao-cung-mau-hong\">hồng</a>&nbsp;fuschia.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Leonie Hanne phối màu trang phục\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/21/439139/leonie-hanne-colorblocking-pink.jpg\" style=\"height:1390px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @leoniehanne)</p>\r\n\r\n<p><img alt=\"Phối color blocking gam màu xanh\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/16/439139/color-block-xanh-leoniehanne.jpg\" style=\"height:936px; width:750px\" /></p>\r\n\r\n<p>(Ảnh: @leoniehanne)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>NINA SANDBECH &ndash; @NINASANDBECH</h2>\r\n\r\n<p>C&aacute;ch phối đồ color-blocking lu&ocirc;n đem lại cho người mặc một nguồn&nbsp;<a href=\"https://www.elle.vn/bi-quyet-song/lam-sao-de-thu-hut-nhung-dieu-tich-cuc-vao-cuoc-song\">năng lượng t&iacute;ch cực</a>&nbsp;đặc biệt bởi những khối m&agrave;u rực rỡ. Bạn chắc chắn cũng sẽ cảm nhận được tinh thần lạc quan n&agrave;y khi gh&eacute; thăm Instagram của c&ocirc; n&agrave;ng Nina Sandbech. L&agrave; một blogger về thời trang với gần 300 ngh&igrave;n lượt theo d&otilde;i, thế giới m&agrave;u sắc của Nina cực kỳ phong ph&uacute;. Ở Nina, ta học được sự đa dạng c&aacute;ch phối color-blocking từ c&aacute;c gam m&agrave;u n&oacute;ng, lạnh một c&aacute;ch đầy tinh tế.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nina Sandbech colorblocking\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/21/439139/nina-sandbech-phoi-do.jpg\" style=\"height:1397px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @ninasandbech)</p>\r\n\r\n<p><img alt=\"Phối trang phục color blocking\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/16/439139/phoi-color-block-ninasandbech.jpg\" style=\"height:933px; width:750px\" /></p>\r\n\r\n<p>(Ảnh: @ninasandbech)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave; một blogger thời trang, Nina kh&ocirc;ng b&oacute; buộc bản th&acirc;n trong một phong c&aacute;ch độc nhất. C&ocirc; n&agrave;ng lu&ocirc;n thỏa sức s&aacute;ng tạo với những &ldquo;khối m&agrave;u&rdquo; rực rỡ tr&ecirc;n những thiết kế v&agrave; chất liệu đa dạng, mới mẻ.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Phối suit kiểu color blocking màu xanh dương\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/16/439139/phoi-color-block-suit-xanh-duong-ninasandbech.jpg\" style=\"height:911px; width:750px\" /></p>\r\n\r\n<p>(Ảnh: @ninasandbech)</p>\r\n\r\n<p><img alt=\"Phối color blocking màu xanh lá và cam\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/16/439139/color-block-xanh-la-cam-ninasandbech.jpg\" style=\"height:925px; width:750px\" /></p>\r\n\r\n<p>(Ảnh: @ninasandbech)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.elle.vn/the-gioi-thoi-trang/tai-khoan-instagram-thoi-trang-phap\" rel=\"article_gallery\" title=\"10 kênh Instagram tín đồ thời trang Pháp không thể bỏ qua\"><img alt=\"10 kênh Instagram tín đồ thời trang Pháp không thể bỏ qua\" src=\"https://www.elle.vn/wp-content/uploads/2020/04/03/395882/10-tai-khoan-instagram-cua-cac-tin-do-thoi-trang-Phap-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/the-gioi-thoi-trang/tai-khoan-instagram-thoi-trang-phap\" title=\"10 kênh Instagram tín đồ thời trang Pháp không thể bỏ qua\">10 k&ecirc;nh Instagram t&iacute;n đồ thời trang Ph&aacute;p kh&ocirc;ng thể bỏ qua</a></p>\r\n\r\n<p>Vẫn mang đậm dấu ấn Parisian Chic nhưng mỗi n&agrave;ng fashionista Ph&aacute;p lại mang một sắc m&agrave;u thời trang ri&ecirc;ng kh&oacute; nhầm lẫn.</p>\r\n\r\n<h2>EMILI SINDLEV &ndash; @EMILISINDLEV&nbsp;</h2>\r\n\r\n<p>L&agrave; một stylist c&oacute; tiếng tại Copenhagen, Emili Sindlev sở hữu cho m&igrave;nh t&agrave;i khoản Instagram với hơn nửa triệu lượt theo d&otilde;i từ giới mộ điệu. Vốn c&oacute; đam m&ecirc; mạnh mẽ với những sắc m&agrave;u rực rỡ, Emili lu&ocirc;n t&igrave;m t&ograve;i cảm hứng cho những bộ c&aacute;nh từ đời thường đến sự kiện của m&igrave;nh từ lối chơi m&agrave;u color-blocking s&agrave;nh điệu.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Phối đồ color blocking màu xanh lá và hồng\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/16/439139/phoi-color-block-xanh-la-hong-emilisindlev.jpg\" style=\"height:928px; width:750px\" /></p>\r\n\r\n<p>(Ảnh: @emilisindlev)</p>\r\n\r\n<p><img alt=\"Phối màu trang phục colorblock\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/21/439139/emilisindlev-colorblcoking.jpg\" style=\"height:1399px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @emilisindlev)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh việc lu&ocirc;n được chi&ecirc;u đ&atilde;i những &ldquo;bữa tiệc&rdquo; thời trang đầy m&atilde;n nh&atilde;n, người theo d&otilde;i c&ocirc; n&agrave;ng c&ograve;n nhận được luồng năng lượng t&iacute;ch cực đến từ những sắc m&agrave;u rực rỡ ngập tr&agrave;n tr&ecirc;n trang Instagram của n&agrave;ng stylist người Đan Mạch.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Phối màu trang phục emili sindlev\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/21/439139/colorblockin-sindlev.jpg\" style=\"height:1389px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @emilisindlev)</p>\r\n\r\n<p><img alt=\"emili sindlev\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/21/439139/emilisindlev-colorblock.jpg\" style=\"height:1397px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @emilisindlev)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>MAXINE WYLDE &ndash; @__MMAXINEWYLDE</h2>\r\n\r\n<p>Maxine Wylde l&agrave; influencer thời trang người &Uacute;c với t&agrave;i khoản Instagram c&oacute; hơn 300 ngh&igrave;n người theo d&otilde;i v&agrave; đồng thời l&agrave; chủ k&ecirc;nh&nbsp;<a href=\"https://www.elle.vn/the-gioi-thoi-trang/mac-dep-moi-ngay-youtuber-chau-a\">Youtube truyền cảm hứng mặc đẹp</a>&nbsp;với hơn 4 ngh&igrave;n subscribers. Nếu Youtube l&agrave; nơi c&ocirc; n&agrave;ng chia sẻ về mẹo phối đồ với những trang phục cơ bản v&agrave; cuộc sống h&agrave;ng ng&agrave;y th&igrave; Instagram của Maxine lại l&agrave; &ldquo;s&acirc;n chơi m&agrave;u sắc&rdquo; rực rỡ với c&aacute;c t&ocirc;ng&nbsp;<a href=\"https://www.elle.vn/xu-huong-phong-cach/mac-dep-cung-sac-xanh-la\">xanh l&aacute;</a>, xanh dương hay đỏ. Maxine thường ho&agrave;n thiện c&aacute;c bản phối thời trang color-blocking của m&igrave;nh c&ugrave;ng phụ kiện gi&agrave;y v&agrave;&nbsp;<a href=\"https://www.elle.vn/the-gioi-thoi-trang/tui-xach-thoi-trang\">t&uacute;i x&aacute;ch</a>&nbsp;nghịch m&agrave;u để nhấn mạnh điểm nh&igrave;n.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Maxine Wylde\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/21/439139/colorblocking-maxinewylde.jpg\" style=\"height:1409px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh:__mmaxinewylde)</p>\r\n\r\n<p><img alt=\"Phối màu color block maixne\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/21/439139/colorblock-maxinewylde.jpg\" style=\"height:1293px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh:__mmaxinewylde)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh những bản phối color-blocking bắt mắt, Instagram của c&ocirc; n&agrave;ng c&ograve;n l&ocirc;i cuốn người xem bởi những gợi &yacute; trang phục xuống phố thời thượng với nhiều phong c&aacute;ch đa dạng rất đ&aacute;ng để thử nghiệm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Maxine wylde\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/21/439139/colorblock-maxine.jpg\" style=\"height:1396px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh:__mmaxinewylde)</p>\r\n\r\n<p><img alt=\"Color blocking Maxine\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/21/439139/maxine-colorblock.jpg\" style=\"height:1406px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh:__mmaxinewylde)</p>\r\n', 'Minh Văn', '2021-06-23 18:41:42', '0000-00-00 00:00:00'),
(2, '#ELLESTAYINGINFASHION – KHI NHỮNG THIẾT KẾ VÁY NGỦ BAY BỔNG “LÊN NGÔI”', 'mac-dep-cung-vay-ngu', 1, 'night-gown-ifonlyif-thumb-445x250.jpg', '<p>Đ&atilde; đến l&uacute;c bạn thay những bộ đồ ở nh&agrave; nh&agrave;m ch&aacute;n, c&oacute; phần xuề xo&agrave;, đơn điệu trong tủ đồ của m&igrave;nh bằng c&aacute;c thiết kế v&aacute;y ngủ nữ t&iacute;nh v&agrave; thời thượng.</p>\r\n', 0, 1, '<p>Khi những ng&agrave;y gi&atilde;n c&aacute;ch dần trở n&ecirc;n quen thuộc th&igrave; giới mộ điệu cũng tự thoả m&atilde;n đam m&ecirc; thời trang của m&igrave;nh bằng việc mặc đẹp kể cả khi ở nh&agrave;. B&ecirc;n cạnh những bộ c&aacute;nh s&agrave;nh điệu cho những tấm h&igrave;nh đăng tải tr&ecirc;n mạng x&atilde; hội, họ cũng t&igrave;m kiếm cho m&igrave;nh những m&oacute;n đồ mặc nh&agrave; thoải m&aacute;i nhưng vẫn phải thật thời trang. V&agrave; c&ograve;n g&igrave; đ&aacute;p ứng đủ c&aacute;c y&ecirc;u cầu đấy hơn một chiếc v&aacute;y ngủ bay bổng đầy nữ t&iacute;nh với c&aacute;c thiết kế, đường may tinh tế, thời thượng?&nbsp;</p>\r\n\r\n<p>Khi nhắc đến v&aacute;y ngủ, một v&agrave;i người sẽ nghĩ ngay đến chiếc&nbsp;<a href=\"https://www.elle.vn/xu-huong-thoi-trang/vay-hoa-mua-he\">v&aacute;y hoa</a>&nbsp;mặc nh&agrave; m&agrave; những người b&agrave;, người mẹ của ch&uacute;ng ta thường mặc. Hoặc, những chiếc đầm thun hoạt h&igrave;nh Disney m&agrave; c&aacute;c đứa ch&aacute;u l&uacute;c n&agrave;o cũng m&ecirc; mẩn. Nhưng bạn y&ecirc;n t&acirc;m, đấy kh&ocirc;ng phải l&agrave; thiết kế v&aacute;y ngủ m&agrave; cả l&agrave;ng thời trang đang &ldquo;ph&aacute;t cuồng&rdquo; l&ecirc;n đ&acirc;u. Những s&aacute;ng tạo m&aacute;t mẻ, đầy lạ mắt v&agrave; thời thượng trong thiết kế v&agrave; m&agrave;u sắc đang xuất hiện khắp c&aacute;c mạng x&atilde; hội mới l&agrave; thứ ELLE muốn giới thiệu đến bạn.&nbsp;</p>\r\n\r\n<p><img alt=\"Nightgown màu cam nhún bèo vai\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/vay-ngu-cam.jpg\" style=\"height:1403px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @matildadjerf)</p>\r\n\r\n<p>Với giới mộ điệu v&agrave; ELLE, một chiếc v&aacute;y ngủ đ&uacute;ng c&aacute;ch sẽ được may từ c&aacute;c loại vải mềm, nhẹ với phom d&aacute;ng rộng r&atilde;i, kh&ocirc;ng b&oacute; s&aacute;t. Đ&acirc;y sẽ l&agrave; thiết kế m&agrave; bạn c&oacute; thể mặc được cho cả những buổi chiều lười biếng ngủ tr&ecirc;n ghế sa l&ocirc;ng, khi đi mua h&agrave;ng tạp h&oacute;a v&agrave; kể cả những buổi họp Zoom c&ugrave;ng đồng nghiệp m&agrave; kh&ocirc;ng cảm thấy ngại ng&ugrave;ng v&igrave; sự xuề xo&agrave;, kh&ocirc;ng chỉn chu. Hơn tất cả, v&aacute;y ngủ n&agrave;y l&agrave; một lựa chọn đ&aacute;ng hoan ngh&ecirc;nh thay cho những bộ đồ ở nh&agrave; vải thun nh&agrave;m ch&aacute;n trong ng&agrave;y H&egrave; oi ả bởi ch&uacute;ng tho&aacute;ng m&aacute;t, rộng r&atilde;i v&agrave; kh&ocirc;ng tốn c&ocirc;ng sức để mặc.&nbsp;</p>\r\n\r\n<p><img alt=\"đầm ngủ màu vàng\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/dam-ngu-hoai-co.jpg\" style=\"height:1394px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @juliaberolzheimer)</p>\r\n\r\n<h2>V&Aacute;Y NGỦ MANG CẢM HỨNG HO&Agrave;I CỔ&nbsp;</h2>\r\n\r\n<p>Sẽ l&agrave; một thiếu x&oacute;t nếu tủ đồ m&ugrave;a gi&atilde;n c&aacute;ch của bạn thiếu đi những chiếc v&aacute;y ngủ mang phong c&aacute;ch&nbsp;<a href=\"https://www.elle.vn/xu-huong-phong-cach/phu-kien-phong-cach-thoi-trang-vintage\">thời trang ho&agrave;i cổ</a>&nbsp;của thời kỳ Victoria. Những thiết kế đơn m&agrave;u tinh giản với phom su&ocirc;ng d&agrave;i, bay bổng đi k&egrave;m chi tiết ren, b&egrave;o nh&uacute;n ở cổ v&agrave; tay &aacute;o sẽ mang đến cho bạn vẻ ngo&agrave;i đầy nữ t&iacute;nh, mềm mại nhưng vẫn sang trọng m&agrave; kh&ocirc;ng hề bị sến.&nbsp;</p>\r\n\r\n<p><img alt=\"váy ngủ cổ rút dây\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/vay-ngu-trang-phu-kien.jpg\" style=\"height:1397px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @chiaratotire)</p>\r\n\r\n<p>Ở c&aacute;c thiết kế n&agrave;y, t&ocirc;ng m&agrave;u trắng nguy&ecirc;n bản sẽ l&agrave; sự lựa chọn ho&agrave;n hảo nhất. Tuy nhi&ecirc;n, bạn cũng c&oacute; thể trải nghiệm những gam&nbsp;<a href=\"https://www.elle.vn/xu-huong-phong-cach/bien-tau-hien-dai-cung-mau-pastel\">pastel</a>&nbsp;nhẹ nh&agrave;ng như xanh cốm,&nbsp;<a href=\"https://www.elle.vn/xu-huong-phong-cach/mix-mau-quan-ao-cung-mau-hong\">hồng</a>,&hellip; để mang lại sự mới lạ cho chiếc v&aacute;y mặc nh&agrave;.&nbsp;</p>\r\n\r\n<p><img alt=\"đầm ren vintage\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/dam-ngu-ren.jpg\" style=\"height:1402px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @nadiiife)</p>\r\n\r\n<h4>GỢI &Yacute; MUA SẮM&nbsp;</h4>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"váy ngủ xanh cốm chi tiết cách điệu\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/vay-ngu-xanh-la.jpg\" style=\"height:1495px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: Shein)</p>\r\n\r\n<p><img alt=\"váy ngủ trắng chi tiết ren\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/vay-ngu-chi-tiet-cach-dieu.jpg\" style=\"height:1500px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: Shein)</p>\r\n\r\n<p><img alt=\"váy ngủ ren trắng \" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/vay-ngu-ren-trang.jpeg\" style=\"height:1383px; width:1031px\" /></p>\r\n\r\n<p>(Ảnh: For Love and Luna)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.elle.vn/xu-huong-phong-cach/quan-mac-nha-mua-gian-cach\" rel=\"article_gallery\" title=\"#ELLEstayinginfashion - 4 thiết kế quần mặc nhà thoải mái cho mùa giãn cách\"><img alt=\"#ELLEstayinginfashion - 4 thiết kế quần mặc nhà thoải mái cho mùa giãn cách\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/08/438068/quan-mac-nha-det-kim-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/xu-huong-phong-cach/quan-mac-nha-mua-gian-cach\" title=\"#ELLEstayinginfashion - 4 thiết kế quần mặc nhà thoải mái cho mùa giãn cách\">#ELLEstayinginfashion - 4 thiết kế quần mặc nh&agrave; thoải m&aacute;i cho m&ugrave;a gi&atilde;n c&aacute;ch</a></p>\r\n\r\n<p>&quot;L&ecirc;n đồ&quot; mặc đẹp tại gia m&ugrave;a gi&atilde;n c&aacute;ch kh&ocirc;ng kh&oacute; khi bạn sở hữu những thiết kế quần mặc nh&agrave; tiện dụng, thoải m&aacute;i v&agrave; thời thượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>PHẦN TAY C&Aacute;CH ĐIỆU NỮ T&Iacute;NH</h2>\r\n\r\n<p>Nếu kh&ocirc;ng th&iacute;ch những thiết kế tay d&agrave;i ho&agrave;i cổ c&oacute; phần bất tiện, bạn h&atilde;y thử ngay kiểu v&aacute;y ngủ với phần tay may c&aacute;nh ti&ecirc;n điệu đ&agrave;. Hoặc cập nhật xu hướng m&ugrave;a H&egrave; với&nbsp;<a href=\"https://www.elle.vn/xu-huong-phong-cach/mix-match-ao-tay-phong\">tay &aacute;o phồng</a>&nbsp;thắt nơ đầy nữ t&iacute;nh, thời thượng.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"váy ngủ tay cánh tiên\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/dam-ngu-khong-tay-hoa-tiet.jpg\" style=\"height:1126px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @hillhouse)</p>\r\n\r\n<p><img alt=\"váy ngủ tay phồng cách điệu\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/dam-ngu-tay-phong-that-no.jpg\" style=\"height:1400px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @elizabethvictoriaclark)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&aacute;y ngủ với thiết kế bẹt vai c&aacute;ch điệu đầy nữ t&iacute;nh, quyến rũ sẽ l&agrave; lựa chọn cho những c&ocirc; n&agrave;ng theo đuổi phong c&aacute;ch &ldquo;b&aacute;nh b&egrave;o&rdquo; mộng mơ.&nbsp;</p>\r\n\r\n<p><img alt=\"đầm bẹt vai satin\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/vay-ngu-tre-vai-satin.jpg\" style=\"height:1393px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @sabinasocol)</p>\r\n\r\n<h3>GỢI &Yacute; MUA SẮM</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Váy ngủ tay cánh tiên\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/for-love-and-luna-vay-ngu.jpeg\" style=\"height:1360px; width:1021px\" /></p>\r\n\r\n<p>(Ảnh: For Love and Luna)</p>\r\n\r\n<p><img alt=\"đầm trắng tay phồng\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/vay-ngu-tay-phong-ifonlyif.jpg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p>(Ảnh: If Only If)</p>\r\n\r\n<p><img alt=\"đầm trắng tay phồng thắt nơ\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/dam-ngu-tay-phong.jpg\" style=\"height:1487px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: Shein)</p>\r\n\r\n<p><img alt=\"Đầm ngủ trễ vai \" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/vay-ngu-tre-vai-mau-vang-satin.jpg\" style=\"height:1732px; width:1152px\" /></p>\r\n\r\n<p>(Ảnh: Hill House Home)</p>\r\n\r\n<p><img alt=\"đầm trễ vai trắng\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/dam-ngu-tre-vai-trang.jpg\" style=\"height:1732px; width:1152px\" /></p>\r\n\r\n<p>(Ảnh: Hill House Home)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>THIẾT KẾ V&Aacute;Y NGỦ PYJAMA LỊCH SỰ</h2>\r\n\r\n<p>V&aacute;y ngủ pyjama với phần cổ sơmi chỉn chu v&agrave; d&aacute;ng v&aacute;y như một chiếc sơmi su&ocirc;ng rộng sẽ l&agrave; lựa chọn lịch sự nhưng vẫn đủ thoải m&aacute;i cho những ng&agrave;y l&agrave;m việc tại nh&agrave; phải họp Zoom c&ugrave;ng đồng nghiệp. Kiểu v&aacute;y n&agrave;y thường c&oacute; chất liệu l&agrave; satin v&agrave; lụa b&oacute;ng sang trọng c&ugrave;ng phần cổ biến tấu mềm mại hơn so với cổ sơmi nguy&ecirc;n bản. Tuy nhi&ecirc;n, bạn vẫn c&oacute; thể lựa chọn v&aacute;y ngủ&nbsp;<a href=\"https://www.elle.vn/xu-huong-phong-cach/5-bi-quyet-mac-dep-khi-dien-trang-phuc-pyjama\">pyjama</a>&nbsp;với chất liệu cotton tho&aacute;ng m&aacute;t v&agrave; dễ chăm s&oacute;c.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"đầm pyjama màu đen \" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/dam-pyjama-den.jpg\" style=\"height:1391px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @al3xha)</p>\r\n\r\n<p><img alt=\"váy ngủ dáng pyjama màu xám \" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/dam-pyjama-xam.jpg\" style=\"height:1397px; width:1125px\" /></p>\r\n\r\n<p>(Ảnh: @michellevanesa)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h4>GỢI &Yacute; MUA SẮM</h4>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"váy ngủ pyjama\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/vay-ngu-pyjama-victoria-secret.jpeg\" style=\"height:1013px; width:760px\" /></p>\r\n\r\n<p>(Ảnh: Victoria Secret)</p>\r\n\r\n<p><img alt=\"váy ngủ Riverisland\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/pink-stripe-print-oversized-mini-bed-shirt_786125_main.jpeg\" style=\"height:1200px; width:1200px\" /></p>\r\n\r\n<p>(Ảnh: River Island)</p>\r\n\r\n<p><img alt=\"đầm pyjama nap loungewear\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/17/439292/pyjama-dress-naploungewear.jpeg\" style=\"height:2505px; width:1837px\" /></p>\r\n\r\n<p>(Ảnh: NAP Loungewear)</p>\r\n', 'Minh Văn', '2021-06-23 18:43:16', '0000-00-00 00:00:00'),
(3, '5 MÓN PHỤ KIỆN CAO CẤP DÀNH CHO TÍN ĐỒ THỜI TRANG TRẺ', 'phu-kien-hang-hieu-cho-gioi-tre', 1, 'phu-kien-thinh-hanh-445x250.jpg', '<p>Sự tiếp nối, kế thừa c&aacute;c gi&aacute; trị kinh điển l&agrave; c&aacute;ch m&agrave; thế giới thời trang được vận h&agrave;nh để hướng đến tương lai. V&agrave; l&agrave;n gi&oacute; mới từ thế hệ người d&ugrave;ng trẻ đ&atilde; trở th&agrave;nh chấ', 0, 1, '<h2>D&Acirc;Y CHUYỀN CHANEL ETERNAL NO.5 ĐẲNG CẤP</h2>\r\n\r\n<p>Để t&ocirc;n vinh v&agrave; kỷ niệm 100 năm nức tiếng của&nbsp;<a href=\"https://www.elle.vn/xu-huong-lam-dep/chanel-no-5-bay-gio-o-day-va-mai-mai\">nước hoa N &deg; 5</a>, nh&agrave; mốt nước Ph&aacute;p Chanel đ&atilde; sử dụng hai chất liệu v&agrave;ng trắng v&agrave; kim cương xa xỉ để t&aacute;i hiện lại h&igrave;nh ảnh con số &ldquo;5&rdquo; huyền thoại. Con số may mắn của Mademoiselle Chanel v&agrave; &ldquo;giọt nước hoa&rdquo; từ&nbsp;<a href=\"https://www.elle.vn/bo-hinh-cua-elle/trang-suc-kim-cuong-nu-hoang-cua-dem\">kim cương</a>&nbsp;đ&atilde; tạo n&ecirc;n thiết kế phụ kiện mặt d&acirc;y chuyền đầy quyến rũ v&agrave; tinh tế.&nbsp;</p>\r\n\r\n<p><img alt=\"phụ kiện cho giới trẻ\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/28/436559/phu-kien-cho-gioi-tre-1024x1391.jpg\" style=\"height:992px; width:730px\" title=\"phụ kiện cho giới trẻ\" /></p>\r\n\r\n<h2>DIORDOUBLE &ndash; PHỤ KIỆN T&Uacute;I X&Aacute;CH THỜI THƯỢNG</h2>\r\n\r\n<p><a href=\"https://www.elle.vn/bo-hinh-cua-elle/tui-xach-va-phu-kien-thoi-trang\">T&uacute;i x&aacute;ch</a>&nbsp;DiorDouble sở hữu thiết kế quai x&aacute;ch mang vẻ đẹp sang trọng như một chiếc d&acirc;y chuyền qu&yacute; ph&aacute;i. Phần d&acirc;y đeo l&agrave; chuỗi li&ecirc;n kết c&aacute;c k&iacute; tự CD biểu tượng của thương hiệu. Th&acirc;n t&uacute;i m&agrave;u đen được chế t&aacute;c từ chất liệu da b&ecirc; mềm mại, mịn tay v&agrave; b&oacute;ng bẩy. Nắp t&uacute;i cố định điểm nh&igrave;n một lần nữa v&agrave;o phần kho&aacute; đ&iacute;nh k&iacute; tự CD.</p>\r\n\r\n<p><img alt=\"phụ kiện túi Diordouble\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/28/436559/phu-kien-tui-Diordouble-1024x1389.jpg\" style=\"height:990px; width:730px\" title=\"phụ kiện túi Diordouble\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.elle.vn/the-gioi-thoi-trang/tui-xach-thoi-trang\" title=\"6 chiếc túi xách thời trang xứng đáng để phái đẹp \"><img alt=\"6 chiếc túi xách thời trang xứng đáng để phái đẹp \" src=\"https://www.elle.vn/wp-content/uploads/2017/05/31/tui-xach-kinh-dien-1-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/the-gioi-thoi-trang/tui-xach-thoi-trang\" title=\"6 chiếc túi xách thời trang xứng đáng để phái đẹp \">6 chiếc t&uacute;i x&aacute;ch thời trang xứng đ&aacute;ng để ph&aacute;i đẹp &quot;đầu tư&quot;</a></p>\r\n\r\n<p>Bạn c&oacute; thể thường nghe đ&acirc;u đ&oacute; những danh s&aacute;ch m&oacute;n đồ thời trang &quot;đắt xắt ra miếng&quot; hay items kh&ocirc;ng bao giờ lỗi mốt. Thực vậy, c&oacute; những thiết kế&nbsp;đ&atilde;...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>GI&Agrave;Y BURBERRY VINTAGE CHECK SLINGBACK HO&Agrave;I CỔ</h2>\r\n\r\n<p>Vẻ đẹp cổ điển của phụ kiện&nbsp;<a href=\"https://www.elle.vn/xu-huong-thoi-trang/giay-slingback-tro-lai\">gi&agrave;y b&iacute;t mũi</a>&nbsp;phủ vải cotton c&agrave;ng th&ecirc;m ấn tượng nhờ v&agrave;o&nbsp;<a href=\"https://www.elle.vn/xu-huong-thoi-trang/hoa-tiet-thoi-trang-he-2021\">họa tiết</a>&nbsp;kẻ &ocirc; trứ danh từ nh&agrave; mốt Anh quốc. Thiết kế sở hữu g&oacute;t gi&agrave;y mảnh kết nối duy&ecirc;n d&aacute;ng với phần mũi thon nhọn. Lớp l&oacute;t da cừu b&ecirc;n trong đem lại cảm gi&aacute;c &ecirc;m &aacute;i cho đ&ocirc;i b&agrave;n ch&acirc;n ph&aacute;i đẹp.&nbsp;</p>\r\n\r\n<p><img alt=\"phụ kiện giày slingback Burberry\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/28/436559/phu-kien-giay-slingback-Burberry-1024x1433.jpg\" style=\"height:1022px; width:730px\" title=\"phụ kiện giày slingback Burberry\" /></p>\r\n\r\n<h2>PHỤ KIỆN GI&Agrave;Y THỂ THAO DOLCE &amp; GABBANA NS1 NĂNG ĐỘNG</h2>\r\n\r\n<p>Điểm độc đ&aacute;o của đ&ocirc;i&nbsp;<a href=\"https://www.elle.vn/tin-thoi-trang/giay-thao-dang-dan-soan-ngoi-giay-cao-got\">sneakers</a>&nbsp;NS1 nh&agrave; Dolce &amp; Gabbana l&agrave; sự phối hợp giữa c&aacute;c chất liệu cao cấp. Thiết kế được phủ bởi da sần, ren, da b&ecirc; v&agrave; sử dụng đế cao su đầy thoải m&aacute;i. Phần th&acirc;n tr&ecirc;n gi&agrave;y l&agrave;m bằng ren v&agrave; da sần với c&aacute;c chi tiết được đan c&agrave;i theo dạng h&igrave;nh tr&ograve;n in t&ecirc;n thương hiệu.</p>\r\n\r\n<p><img alt=\"giày thể thao Dolce Gabbana\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/28/436559/phu-kien-giay-the-thao-Dolce-Gabbana-1024x1389.jpg\" style=\"height:990px; width:730px\" title=\"phụ kiện giàythể thao Dolce Gabbana\" /></p>\r\n\r\n<h2>T&Uacute;I GUCCI HORSEBIT 1955 S&Agrave;NH ĐIỆU</h2>\r\n\r\n<p>Mẫu t&uacute;i Gucci Horsebit 1955 d&agrave;nh ri&ecirc;ng cho thị trường Việt Nam được l&agrave;m từ chất liệu da c&aacute; sấu m&agrave;u xanh &ldquo;water green&rdquo; đầy mới lạ v&agrave; độc đ&aacute;o. M&oacute;n phụ kiện thời trang n&agrave;y được nh&agrave; mốt nước &Yacute; giữ nguy&ecirc;n thiết kế nguy&ecirc;n bản đặc trưng của d&ograve;ng t&uacute;i Horsebit 1955 trứ danh. Được biết, phi&ecirc;n bản đặc biệt n&agrave;y chỉ c&oacute; ba chiếc tương tự tr&ecirc;n to&agrave;n thế giới.</p>\r\n\r\n<p><img alt=\" túi Gucci Horsebit\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/28/436559/phu-kien-tui-Gucci-Horsebit-1024x1412.jpg\" style=\"height:1007px; width:730px\" title=\"phụ kiện túi Gucci Horsebit\" /></p>\r\n', 'Minh Văn', '2021-06-23 18:44:27', '0000-00-00 00:00:00'),
(4, '5 THIẾT KẾ TÚI XÁCH ĐÓN MÙA THU BẠN KHÔNG THỂ BỎ LỠ', 'xu-huong-tui-xach-don-thu', 1, 'chain-bag-thumbnail-445x250.jpg', '<p>C&ugrave;ng ELLE&nbsp;tự tin &ldquo;bước&rdquo; v&agrave;o m&ugrave;a Thu c&ugrave;ng những chiếc t&uacute;i x&aacute;ch &ldquo;chuẩn&rdquo; xu hướng năm nay.</p>\r\n', 0, 1, '<p>M&ugrave;a Hạ n&oacute;ng bức đang dần đi qua, nhường chỗ lại cho những ng&agrave;y Thu m&aacute;t mẻ, dễ chịu. Khi c&aacute;c m&ugrave;a trong năm thay đổi, tủ đồ của ch&uacute;ng ta cũng được &ldquo;l&agrave;m mới&rdquo; để ph&ugrave; hợp hơn với thời tiết. B&ecirc;n cạnh việc cập nhật những bộ trang phục mới, đừng qu&ecirc;n sắm cho m&igrave;nh những chiếc t&uacute;i x&aacute;ch được giới mộ điệu thế giới ưu &aacute;i.&nbsp;</p>\r\n\r\n<h2>T&Uacute;I QUAI X&Iacute;CH BẢN TO</h2>\r\n\r\n<p>Nếu l&agrave; một người y&ecirc;u thời trang, bạn hẳn kh&ocirc;ng thể qu&ecirc;n được chiếc t&uacute;i x&aacute;ch với phần quai x&iacute;ch bản to ph&aacute; c&aacute;ch của Bottega Veneta. Sau khi được nh&agrave; mốt n&agrave;y &ldquo;lăng x&ecirc;&rdquo; th&agrave;nh c&ocirc;ng v&agrave;o năm 2020, quai x&iacute;ch bản to lại một lần nữa &ldquo;l&agrave;m mưa l&agrave;m gi&oacute;&rdquo; v&agrave; trở th&agrave;nh nguồn cảm hứng s&aacute;ng tạo cho c&aacute;c thiết kế t&uacute;i x&aacute;ch của c&aacute;c thương hiệu thời trang lớn, b&eacute; trong năm nay. Năm nay, ngo&agrave;i chất liệu kim loại, th&igrave; những&nbsp;<a href=\"https://www.elle.vn/xu-huong-thoi-trang/cach-phoi-vong-tay-phu-kien-giong-rose\">mắt x&iacute;ch</a>&nbsp;từ nhựa được phủ sơn acrylic m&agrave;u sắc cũng l&agrave; lựa chọn được nhiều nh&atilde;n h&agrave;ng thời trang ưa chuộng.&nbsp;</p>\r\n\r\n<p><img alt=\"Túi quai xích trắng Bottega Veneta\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-quai-xich-trang-bottega-veneta.jpeg\" style=\"height:1230px; width:1080px\" /></p>\r\n\r\n<p>(Ảnh: @vikyrader)</p>\r\n\r\n<h2>GỢI &Yacute; MUA SẮM:</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Túi quai xích Zara\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-quai-xich-hot-pink-zara-scaled.jpeg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p>(Ảnh: Zara)</p>\r\n\r\n<p><img alt=\"Chain bag Charles &amp; Keith\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-quai-xich-charles-keith.jpeg\" style=\"height:1536px; width:1152px\" /></p>\r\n\r\n<p>(Ảnh: Charles &amp; Keith)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>T&Uacute;I X&Aacute;CH D&Aacute;NG TẬP T&Agrave;I LIỆU</h2>\r\n\r\n<p>Tr&ecirc;n c&aacute;c s&agrave;n diễn thời trang năm nay, kh&ocirc;ng kh&oacute; để ch&uacute;ng ta bắt gặp h&igrave;nh ảnh của những chiếc t&uacute;i tr&ocirc;ng như những tập đựng t&agrave;i liệu Manila cũ kĩ. Từ Hermes đến Jil Sander, tất cả đều cho ra mắt chiếc t&uacute;i x&aacute;ch với thiết kế mỏng, nhẹ c&ugrave;ng hiệu ứng tạo phom 2D tinh tế. Do đặc trưng thiết kế, kiểu t&uacute;i n&agrave;y thường chỉ c&oacute; một ngăn v&agrave; kh&ocirc;ng đựng được nhiều đồ. Đ&acirc;y cũng l&agrave; một điểm trừ đ&aacute;ng lưu &yacute; cho c&ocirc; n&agrave;ng n&agrave;o đang c&oacute; dự định &ldquo;tậu&rdquo; chiếc t&uacute;i n&agrave;y.</p>\r\n\r\n<p><img alt=\"Túi xách 2D Hermes\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-2d-folder-bag-hermes.jpeg\" style=\"height:1152px; width:768px\" /></p>\r\n\r\n<p>(Ảnh: Hermes)</p>\r\n\r\n<h4>GỢI &Yacute; MUA SẮM:</h4>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Túi 2D Pedro\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-dung-tai-lieu-pedro.jpeg\" style=\"height:1536px; width:1152px\" /></p>\r\n\r\n<p>(Ảnh: Pedro)</p>\r\n\r\n<p><img alt=\"Túi đựng tài liệu Pedro\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-tai-lieu-trang-pedro.jpeg\" style=\"height:1024px; width:828px\" /></p>\r\n\r\n<p>(Ảnh: Pedro)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>T&Uacute;I D&Aacute;NG B&Aacute;N NGUYỆT</h2>\r\n\r\n<p>Những chiếc t&uacute;i h&igrave;nh d&aacute;ng b&aacute;n nguyệt l&agrave; m&oacute;n&nbsp;<a href=\"https://www.elle.vn/bo-hinh-cua-elle/tui-xach-va-phu-kien-thoi-trang\">phụ kiện</a>&nbsp;thời thượng m&agrave; bất k&igrave; c&ocirc; g&aacute;i n&agrave;o cũng n&ecirc;n sở hữu. Với thiết kế quai cầm vừa vặn, ho&agrave;n hảo v&agrave; phom d&aacute;ng cứng c&aacute;p, tinh tế, bạn c&oacute; thể vừa x&aacute;ch tay vừa kẹp n&aacute;ch một c&aacute;ch dễ d&agrave;ng kiểu t&uacute;i n&agrave;y.&nbsp;</p>\r\n\r\n<p><img alt=\"Túi bán nguyệt Staud\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-ban-nguyet-purseblog.jpeg\" style=\"height:1500px; width:1000px\" /></p>\r\n\r\n<p>(Ảnh: Purse Blog)</p>\r\n\r\n<h4>GỢI &Yacute; MUA SẮM:</h4>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Túi xách Zara hình bán nguyệt\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-ban-nguyet-satin-zara-scaled.jpeg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p>(Ảnh: Zara)</p>\r\n\r\n<p><img alt=\"Túi xách bán nguyệt Pedro\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-ban-nguyet-pedro-lilac.jpeg\" style=\"height:1536px; width:1152px\" /></p>\r\n\r\n<p>(Ảnh: Pedro)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://www.elle.vn/the-gioi-thoi-trang/tui-xach-thoi-trang\" title=\"6 chiếc túi xách thời trang xứng đáng để phái đẹp \"><img alt=\"6 chiếc túi xách thời trang xứng đáng để phái đẹp \" src=\"https://www.elle.vn/wp-content/uploads/2017/05/31/tui-xach-kinh-dien-1-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/the-gioi-thoi-trang/tui-xach-thoi-trang\" title=\"6 chiếc túi xách thời trang xứng đáng để phái đẹp \">6 chiếc t&uacute;i x&aacute;ch thời trang xứng đ&aacute;ng để ph&aacute;i đẹp &quot;đầu tư&quot;</a></p>\r\n\r\n<p>Bạn c&oacute; thể thường nghe đ&acirc;u đ&oacute; những danh s&aacute;ch m&oacute;n đồ thời trang &quot;đắt xắt ra miếng&quot; hay items kh&ocirc;ng bao giờ lỗi mốt. Thực vậy, c&oacute; những thiết kế&nbsp;đ&atilde;...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>T&Uacute;I ĐA NĂNG&nbsp;</h2>\r\n\r\n<p>Đ&acirc;y sẽ l&agrave; kiểu t&uacute;i ho&agrave;n hảo cho những c&ocirc; n&agrave;ng nhiều đồ lại c&oacute; ch&uacute;t &ldquo;hậu đậu&rdquo;, thường lạc mất đồ b&ecirc;n trong những chiếc t&uacute;i 1-2 ngăn cơ bản. Với thiết kế nhiều ngăn t&aacute;ch biệt, d&aacute;ng t&uacute;i x&aacute;ch n&agrave;y gi&uacute;p người d&ugrave;ng dễ d&agrave;ng ph&acirc;n chia đồ đạc của m&igrave;nh, đặc biệt l&agrave; những m&oacute;n đồ nhỏ, dễ đ&aacute;nh rơi, bỏ s&oacute;t. Những chiếc t&uacute;i đa năng cũng gi&uacute;p ph&aacute;i đẹp hạn chế việc phải &ldquo;nhồi nh&eacute;t&rdquo; th&ecirc;m nhiều chiếc t&uacute;i, v&iacute; nhỏ đựng đồ v&agrave;o t&uacute;i x&aacute;ch của m&igrave;nh dẫn đến việc t&uacute;i dễ mất phom d&aacute;ng ban đầu.&nbsp;</p>\r\n\r\n<p><img alt=\"Túi xách utility Louis Vuitton\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/utility-bag-LV.jpeg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p>(Ảnh: Louis Vuitton)</p>\r\n\r\n<h4>GỢI &Yacute; MUA SẮM:</h4>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\" uitility bag pedro\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/multi-bag-pedro.jpeg\" style=\"height:1536px; width:1152px\" /></p>\r\n\r\n<p>(Ảnh: Pedro)</p>\r\n\r\n<p><img alt=\"túi xách Charles &amp; Keith\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/multility-bag-charles-keith.jpeg\" style=\"height:1536px; width:1152px\" /></p>\r\n\r\n<p>(Ảnh: Charles &amp; Keith)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>T&Uacute;I X&Aacute;CH OVERSIZED&nbsp;</h2>\r\n\r\n<p>Những chiếc t&uacute;i x&aacute;ch oversized lu&ocirc;n l&agrave; lựa chọn ho&agrave;n hảo cho những c&ocirc; n&agrave;ng muốn &ldquo;nh&eacute;t cả thế giới&rdquo; v&agrave;o chiếc t&uacute;i của m&igrave;nh. Sau v&agrave;i năm vắng b&oacute;ng tr&ecirc;n c&aacute;c s&agrave;n diễn, năm nay, kiểu t&uacute;i n&agrave;y đ&atilde; quay trở lại v&agrave; &ldquo;tung ho&agrave;nh&rdquo; l&agrave;ng thời trang với những thiết kế c&oacute; chất liệu da trơn tinh tế, thời thượng.&nbsp;</p>\r\n\r\n<p><img alt=\"oversize bag Simon Miller\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-oversize-simon-miller.jpeg\" style=\"height:1466px; width:980px\" /></p>\r\n\r\n<p>(Ảnh: Simon Miller)</p>\r\n\r\n<h4>GỢI &Yacute; MUA SẮM:</h4>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Maxi tote bag Zara\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-tote-oversized-zara-scaled.jpeg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p>(Ảnh: Zara)</p>\r\n\r\n<p><img alt=\"Oversized bag Charles &amp; Keith\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/09/438202/tui-xach-oversize-charles-keith.jpeg\" style=\"height:1536px; width:1152px\" /></p>\r\n\r\n<p>(Ảnh: Charles &amp; Keith)</p>\r\n', 'Minh Văn', '2021-06-23 18:47:47', '0000-00-00 00:00:00'),
(5, 'VẢI DÙ – CHẤT LIỆU “SÁNG GIÁ” DÀNH CHO NGÀY MƯA', 'vai-du-chat-lieu-ngay-mua', 1, 'thumb-vai-du-445x250.jpg', '<p>Vải d&ugrave; l&agrave; chất liệu lu&ocirc;n được ưa chuộng v&agrave;o m&ugrave;a mưa ẩm ướt bởi ưu điểm kh&ocirc;ng thấm nước nổi trội. C&aacute;c thiết kế thời trang từ vải d&ugrave; b&acirc;y giờ cũng được đầu tư hơn về thiết kế v&agrave; m&agrave;u', 0, 1, '<p>Thời tiết miền Nam đang trong những ng&agrave;y giao m&ugrave;a từ Hạ sang Thu, khi m&agrave; nắng n&oacute;ng cực điểm dần nhường chỗ cho những cơn mưa nặng hạt đầu m&ugrave;a. Trước ảnh hưởng của thời tiết, c&aacute;c thiết kế quần &aacute;o v&agrave; phụ kiện c&oacute; khả năng chống thấm nước trở th&agrave;nh ưu ti&ecirc;n h&agrave;ng đầu trong c&aacute;c c&ocirc;ng cuộc mua sắm thời trang. Bởi lẽ, kh&ocirc;ng ai muốn bộ trang phục của m&igrave;nh ướt sũng v&agrave; lấm lem sau một cơn mưa cả. Đ&acirc;y cũng l&agrave; thời điểm m&agrave; những thiết kế với chất liệu vải d&ugrave; chống thấm tốt trở th&agrave;nh ứng cử vi&ecirc;n s&aacute;ng gi&aacute; được nhiều người săn đ&oacute;n.</p>\r\n\r\n<p><img alt=\"Vải dù Raeburn\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/03/437338/thoi-trang-vai-du-raeburn.jpeg\" style=\"height:1920px; width:1280px\" /></p>\r\n\r\n<p>(Ảnh: Raeburn)</p>\r\n\r\n<p>Về mặt vật l&yacute;, vải d&ugrave; l&agrave; tổ hợp của từ 2 đến 3 c&aacute;c nguy&ecirc;n liệu kh&aacute;c nhau như sợi nylon, polyeste, cotton hoặc tơ nh&acirc;n tạo. Do đ&oacute;, chất liệu n&agrave;y c&oacute; những ưu điểm vượt trội như chống thấm nước v&agrave; thớ vải bền chắc. Tuy nhi&ecirc;n, b&ecirc;n cạnh c&aacute;c ưu điểm kể tr&ecirc;n th&igrave; vải d&ugrave; cũng c&oacute; một nhược điểm lớn khi dễ g&acirc;y b&iacute; cho da. V&igrave; thế, c&aacute;c m&oacute;n thời trang l&agrave;m từ vải d&ugrave; thường chỉ được &ldquo;ưu &aacute;i&rdquo; v&agrave;o m&ugrave;a mưa hoặc khi thời tiết m&aacute;t mẻ, dễ chịu.&nbsp;&nbsp;</p>\r\n\r\n<h2>QUẦN VẢI D&Ugrave; S&Agrave;NH ĐIỆU</h2>\r\n\r\n<p>D&agrave;nh cho những ng&agrave;y mưa nhưng vẫn cần ra phố, thiết kế quần vải d&ugrave; sẽ l&agrave; lựa chọn ho&agrave;n hảo cho bạn. Với ưu điểm kh&ocirc;ng thấm nước, quần vải d&ugrave; sẽ &ldquo;bảo vệ&rdquo; bạn trước những hạt mưa v&agrave; tr&aacute;nh quần &aacute;o lấm lem bởi c&aacute;c vũng nước tr&ecirc;n đường. B&ecirc;n cạnh đ&oacute;, với thiết kế đa dạng từ quần cạp cao ống su&ocirc;ng tới những chiếc quần jogger năng động, bạn ho&agrave;n to&agrave;n c&oacute; thể xuống phố thật phong c&aacute;ch v&agrave; thời thượng với quần d&ugrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Quần dù trắng \" src=\"https://www.elle.vn/wp-content/uploads/2021/06/02/437338/quan-du-trang-dionlee.jpg\" style=\"height:923px; width:750px\" /></p>\r\n\r\n<p>(Ảnh: @dionlee)</p>\r\n\r\n<p><img alt=\"Set quần áo dù \" src=\"https://www.elle.vn/wp-content/uploads/2021/06/02/437338/quan-du-venedaacarter.jpg\" style=\"height:744px; width:750px\" /></p>\r\n\r\n<p>(Ảnh: @venedaacarter)</p>\r\n\r\n<p><img alt=\"Quần dù beige phối áo hai dây\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/03/437338/quan-du-beige-dionlee.jpg\" style=\"height:1600px; width:1000px\" /></p>\r\n\r\n<p>(Ảnh: Dion Lee)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh những thiết kế quần thể thao năng động, trang phục &aacute;o liền quần từ chất liệu vải d&ugrave; cũng l&agrave; m&oacute;n đồ đ&aacute;ng thử trong những ng&agrave;y mưa. H&atilde;y mặc thiết kế n&agrave;y ở ngo&agrave;i &aacute;o ba lỗ đơn m&agrave;u để vừa bảo vệ trang phục b&ecirc;n trong những vẫn tr&ocirc;ng thật s&agrave;nh điệu.</p>\r\n\r\n<p><img alt=\"Jump suit màu hồng pastel\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/03/437338/jum-suit-shopstye-scaled.jpg\" style=\"height:1278px; width:852px\" /></p>\r\n\r\n<p>(Ảnh: Shopstyle)</p>\r\n\r\n<p><a href=\"https://www.elle.vn/xu-huong-thoi-trang/trao-luu-ao-khoac-di-mua-sac-mau-giua-tiet-troi-kho-doan\" rel=\"article_gallery\" title=\"Trào lưu áo khoác đi mưa sắc màu giữa tiết trời khó đoán\"><img alt=\"Trào lưu áo khoác đi mưa sắc màu giữa tiết trời khó đoán\" src=\"https://www.elle.vn/wp-content/uploads/2017/05/17/ao-khoac-di-mua-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/xu-huong-thoi-trang/trao-luu-ao-khoac-di-mua-sac-mau-giua-tiet-troi-kho-doan\" title=\"Trào lưu áo khoác đi mưa sắc màu giữa tiết trời khó đoán\">Tr&agrave;o lưu &aacute;o kho&aacute;c đi mưa sắc m&agrave;u giữa tiết trời kh&oacute; đo&aacute;n</a></p>\r\n\r\n<p>Sự thay đổi kh&iacute; hậu ng&agrave;y c&agrave;ng khắc nghiệt v&agrave; thời tiết lu&ocirc;n thất thường kh&oacute; đo&aacute;n, những th&aacute;ng đang l&yacute; ra phải nắng ch&oacute;i chang th&igrave; lại mưa &agrave;o &agrave;o....</p>\r\n\r\n<h2>&Aacute;O KHO&Aacute;C D&Ugrave;&nbsp;NĂNG ĐỘNG</h2>\r\n\r\n<p>B&ecirc;n cạnh ưu điểm chống thấm tốt, &aacute;o kho&aacute;c l&agrave;m từ chất liệu vải d&ugrave; c&ograve;n đem lại n&eacute;t thể thao, năng động cho những người y&ecirc;u thời trang. Bạn c&oacute; thể chọn &aacute;o kho&aacute;c d&aacute;ng lửng&nbsp;<a href=\"https://www.elle.vn/xu-huong-phong-cach/ao-crop-top-thu-dong\">crop top</a>&nbsp;với m&agrave;u sắc tươi s&aacute;ng để xua đi sự &acirc;m u của thời tiết.&nbsp;</p>\r\n\r\n<p><img alt=\"Áo khoác thể thao adidas trắng\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/02/437338/ao-khoac-vai-du-@magdawiethenin.jpg\" style=\"height:921px; width:737px\" /></p>\r\n\r\n<p>(Ảnh: @magdawiethenin)</p>\r\n\r\n<p><img alt=\"Set đồ thể thao vải dù màu hồng \" src=\"https://www.elle.vn/wp-content/uploads/2021/06/02/437338/set-the-thao-hong-vai-du-zara.png\" style=\"height:1174px; width:786px\" /></p>\r\n\r\n<p>(Ảnh: Zara)</p>\r\n\r\n<h2>PHỤ KIỆN T&Uacute;I CHỐNG THẤM</h2>\r\n\r\n<p>V&agrave;o những ng&agrave;y trời trở mưa bất chợt tr&ecirc;n phố, những chiếc ba l&ocirc; hay&nbsp;<a href=\"https://www.elle.vn/the-gioi-thoi-trang/tui-xach-nu-theo-tung-su-kien\">t&uacute;i x&aacute;ch</a>&nbsp;của bạn sẽ kh&ocirc;ng thể tr&aacute;nh khỏi t&igrave;nh trạng bị d&iacute;nh những vệt nước bẩn xấu x&iacute;. L&acirc;u ng&agrave;y, những vết ố sẽ khiến thớ vải của t&uacute;i sẽ kh&ocirc;ng c&ograve;n bền chắc, sỉn m&agrave;u hoặc xuống cấp nhanh ch&oacute;ng. V&igrave; vậy, một chiếc t&uacute;i từ chất liệu chống thấm d&agrave;nh ri&ecirc;ng cho những ng&agrave;y thời tiết &ldquo;ẩm ương&rdquo; sẽ l&agrave; khoản đầu tư thời trang đ&uacute;ng đắn.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Túi xách chống thấm màu nâu Zara\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/02/437338/tui-xach-vai-du-zara-scaled.jpg\" style=\"height:1224px; width:816px\" /></p>\r\n\r\n<p>(Ảnh: Zara)</p>\r\n\r\n<p><img alt=\"Túi chống thấm nước Zara\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/02/437338/bo-doi-tui-cham-tham-zara.png\" style=\"height:1165px; width:769px\" /></p>\r\n\r\n<p>(Ảnh: Zara)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với c&aacute;c thiết kế ng&agrave;y c&agrave;ng đa dạng v&agrave; hợp thời từ c&aacute;c nh&agrave; mốt b&igrave;nh d&acirc;n, bạn ho&agrave;n to&agrave;n c&oacute; thể dễ d&agrave;ng lựa chọn cho m&igrave;nh một chiếc t&uacute;i chống thấm nước hữu dụng nhưng vẫn thật thời trang.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Ba lô vải chống thấm xanh rêu\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/03/437338/Ba-lo-vai-du-xanh-reu-Parfois-scaled.jpg\" style=\"height:2560px; width:1915px\" /></p>\r\n\r\n<p>(Ảnh: Parfois)</p>\r\n\r\n<p><img alt=\"Ba lô chống thấm hoạt hình\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/03/437338/Ba-lo-Parfois-scaled.jpg\" style=\"height:2560px; width:1915px\" /></p>\r\n\r\n<p>(Ảnh: Parfois)</p>\r\n', 'Minh Văn', '2021-06-23 18:50:14', '0000-00-00 00:00:00');
INSERT INTO `daovanminh_post` (`postId`, `postTitle`, `alias`, `topicId`, `image`, `summary`, `trash`, `status`, `detail`, `createBy`, `createDate`, `updateDate`) VALUES
(6, 'DIOR CRUISE 2022 – KHI NHỮNG NỮ THẦN ĐẤT ATHENS “XUYÊN KHÔNG” ĐẾN THẾ GIỚI HIỆN ĐẠI', 'bst-dior-cruise-2022', 2, 'dior-cruise-2022-finale-thumb-445x250.jpg', '<p>Khoảnh khắc ph&aacute;o hoa nở rộ tr&ecirc;n bầu trời Athens trong show diễn Dior Cruise 2022 tựa như lời kết ho&agrave;n mỹ cho c&acirc;u chuyện thời trang t&ocirc;n vinh ph&aacute;i nữ mang đầy gi&aacute; trị nh&acirc;n văn v&agrave; lịch sử của nh&a', 0, 1, '<p>Thủ đ&ocirc; Athens &ndash; c&aacute;i n&ocirc;i của nền văn minh phương T&acirc;y v&agrave; nghệ thuật Ch&acirc;u &Acirc;u đ&atilde; được Dior chọn l&agrave;m nơi tr&igrave;nh diễn v&agrave; giới thiệu những thiết kế mới nhất nhằm ghi nhận v&agrave; t&ocirc;n vinh tầm quan trọng của phụ nữ trong suốt c&aacute;c thời đại. Để thực hiện mong muốn đ&oacute;, Gi&aacute;m đốc S&aacute;ng tạo của Dior đ&atilde; chu du khắp Hy Lạp v&agrave; t&igrave;m t&ograve;i cảm hứng từ những t&agrave;n t&iacute;ch, gi&aacute; trị văn ho&aacute; cổ xưa, kh&aacute;m ph&aacute; những đ&oacute;ng g&oacute;p của phụ nữ trong suốt lịch sử. Từ đ&oacute;, bản &ldquo;giao hưởng thời trang&rdquo; Dior Cruise 2022 ra đời với sự kết hợp đầy h&agrave;i ho&agrave; giữa thời trang cao cấp ho&agrave;n mỹ, phong c&aacute;ch thể thao ph&oacute;ng kho&aacute;ng với những trang phục truyền thống Hy Lạp đầy ho&agrave;i niệm.&nbsp;</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"315\" id=\"618031215\" src=\"https://www.youtube.com/embed/ifzhxnB2Znk?enablejsapi=1&amp;origin=https%3A%2F%2Fwww.elle.vn\" title=\"YouTube video player\" width=\"560\"></iframe></p>\r\n\r\n<p>S&acirc;n vận động Panathenaic ở Athens với những ng&ocirc;i đền ở Acropolis v&agrave; Parthenon sừng sững ở ph&iacute;a xa l&agrave; sự lựa chọn ho&agrave;n hảo v&agrave; &ldquo;nguy&ecirc;n sơ&rdquo; nhất để Dior t&aacute;i hiện h&igrave;nh ảnh c&aacute;c nữ thần Hy Lạp với quyền năng mạnh mẽ. BST Cruise 2022 tr&igrave;nh diễn tr&ecirc;n bối cảnh lịch sử của nền văn minh Hy Lạp cổ đại kh&ocirc;ng chỉ đơn thuần l&agrave; &ldquo;tuy&ecirc;n ng&ocirc;n&rdquo; của Dior cho những n&eacute;t chuyển động tự do của cơ thể ph&aacute;i nữ, m&agrave; đ&acirc;y c&ograve;n l&agrave; lời ca ngợi m&agrave; Chiuri v&agrave; nh&agrave; mốt nước Ph&aacute;p ưu &aacute;i d&agrave;nh cho đất nước n&agrave;y.&nbsp;</p>\r\n\r\n<p><img alt=\"sân vận động Panathenaic\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439546/CRUISE_2022_SCENOGRAPHY25C225A9GEORGE_MESSARITAKIS_1325201.jpeg\" style=\"height:1200px; width:1600px\" /></p>\r\n\r\n<p>S&acirc;n vận động Panathenaic tại Athens</p>\r\n\r\n<h2>&ldquo;HIỆN ĐẠI HO&Aacute;&rdquo; TINH THẦN HY LẠP CỔ ĐẠI</h2>\r\n\r\n<p>Tại m&ugrave;a Cruise 2022,&nbsp;<a href=\"https://www.elle.vn/tin-thoi-trang/thuong-hieu-dior-duoi-thoi-maria-grazia-chiuri-se-co-gi-moi\">Maria Grazia Chiuri</a>&nbsp;đ&atilde; th&agrave;nh c&ocirc;ng t&aacute;i hiện h&igrave;nh ảnh c&aacute;c vị nữ thần v&agrave; phụ nữ Hy Lạp cổ đại qua những thiết kế biến thể của &ldquo;peplos&rdquo; &ndash; loại xi&ecirc;m y truyền thống mang sắc trắng kem đầy thanh lịch v&agrave; thướt tha của đất nước n&agrave;y. Đặc biệt, c&aacute;c bản phối trang phục đầy sự ho&agrave;i cổ n&agrave;y được NTK kết hợp c&ugrave;ng&nbsp;<a href=\"https://www.elle.vn/the-gioi-thoi-trang/nhung-kieu-that-day-giay-doc-dao\">sneakers</a>&nbsp;khoẻ khoắn &ndash; m&oacute;n đồ thời trang chỉ xuất hiện ở thời hiện đại. Đ&acirc;y được xem l&agrave; sự giao thoa ho&agrave;n hảo giữa những gi&aacute; trị hiện tại v&agrave; di sản xưa cũ m&agrave; Dior muốn đem lại cho người xem.&nbsp;</p>\r\n\r\n<p><img alt=\"peplos\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439546/Dior-R22-004.jpeg\" style=\"height:1572px; width:1048px\" /></p>\r\n\r\n<p><img alt=\"dior cruise look 84\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439546/dior-cruise-2022-84.jpeg\" style=\"height:1500px; width:1000px\" /></p>\r\n\r\n<h2>MANG TINH HOA THỜI TRANG TỪ C&Aacute;C NGHỆ NH&Acirc;N ĐỊA PHƯƠNG L&Ecirc;N S&Agrave;N DIỄN</h2>\r\n\r\n<p>Để khắc hoạ chi tiết nhất &ldquo;linh hồn&rdquo; của Hy Lạp cổ đại, Maria Grazia Chiuri đ&atilde; đặc biệt t&igrave;m đến b&agrave;n tay l&agrave;nh nghề của c&aacute;c nghệ nh&acirc;n địa phương. Thợ may Aristeidis Tzonevraki đ&atilde; gi&uacute;p b&agrave; truyền tải vẻ đặc sắc trong kĩ thuật th&ecirc;u tr&ecirc;n Book Tote v&agrave; Bar Jacket. Nh&agrave; m&aacute;y Silk Line, với trụ sở tại Soufli, đ&atilde; hỗ trợ Chiuri trong việc thể hiện hoạ tiết&nbsp;<a href=\"https://www.elle.vn/the-gioi-thoi-trang/phan-biet-hoa-tiet-ke-o-kinh-dien\">Houndstooth</a>&nbsp;đặc trưng của Dior. Chiếc&nbsp;<a href=\"https://www.elle.vn/xu-huong-thoi-trang/mu-xo-phu-kien-mua-he\">mũ ngư d&acirc;n</a>&nbsp;của nh&agrave; may gia truyền Atelier Tsalavoutas &ndash; biểu tượng đặc trưng của ngư d&acirc;n đảo Hydra từ giữa thế kỷ 19 cũng được nữ Gi&aacute;m đốc S&aacute;ng tạo của Dior l&agrave;m mới lại với sự trợ gi&uacute;p của NE.M.A., một thợ may chuy&ecirc;n về kỹ thuật tạo những chi tiết bện thắt tua rua kim tuyến cổ điển.&nbsp;</p>\r\n\r\n<p><img alt=\"CD Resort 2022 Bar Jacket\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-cruise-22-bar-jacket-scaled.jpeg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p><img alt=\"Xưởng thêu lụa Soufli\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-cruise-22-soufli.jpeg\" style=\"height:1536px; width:1954px\" /></p>\r\n\r\n<p><img alt=\"CD Cruise 22\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-cruise-22-non-ngu-dan.jpeg\" style=\"height:1961px; width:1536px\" /></p>\r\n\r\n<p><a href=\"https://www.elle.vn/bo-suu-tap/bo-suu-tap-dior-cruise-2021\" rel=\"article_gallery\" title=\"BST Dior Cruise 2021 khơi nguồn cảm hứng thời trang đồng quê\"><img alt=\"BST Dior Cruise 2021 khơi nguồn cảm hứng thời trang đồng quê\" src=\"https://www.elle.vn/wp-content/uploads/2020/08/07/410660/BST-Dior-Cruise-2021-feature-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/bo-suu-tap/bo-suu-tap-dior-cruise-2021\" title=\"BST Dior Cruise 2021 khơi nguồn cảm hứng thời trang đồng quê\">BST Dior Cruise 2021 khơi nguồn cảm hứng thời trang đồng qu&ecirc;</a></p>\r\n\r\n<p>BST Dior Cruise 2021 đưa người xem trở về qu&ecirc; nh&agrave; Puglia của Gi&aacute;m đốc s&aacute;ng tạo Maria Grazia Chiuri v&agrave; c&oacute; cuộc h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; l&agrave;ng nghề thủ...</p>\r\n\r\n<h2>DIOR CRUISE 2022 &ndash; &ldquo;BẢN GIAO HƯỞNG THỜI TRANG&rdquo; VƯỢT THỜI ĐẠI</h2>\r\n\r\n<p>Quả kh&ocirc;ng ngoa khi n&oacute;i, Maria Chiuri v&agrave; Dior đ&atilde; vẽ n&ecirc;n một bức tranh thần thoại ho&agrave;n mỹ, mang đậm dấu ấn văn minh Hy Lạp xưa cũ nhưng cũng đầy ph&oacute;ng kho&aacute;ng, hiện đại ngay tại s&acirc;n vận động Panathenaic. Xuy&ecirc;n suốt show diễn, giới mộ điệu như bị m&ecirc; hoặc bởi h&igrave;nh ảnh người mẫu sải bước dọc đường băng như những nữ thần quyền năng tr&ecirc;n đỉnh Olympus huyền b&iacute;.</p>\r\n\r\n<p>Khi th&igrave; họ thướt tha với những thiết kế đầm d&agrave;i bay bổng với nếp gấp fluting lả lướt, được phối kh&eacute;o l&eacute;o c&ugrave;ng phụ kiện t&oacute;c,&nbsp;<a href=\"https://www.elle.vn/xu-huong-phong-cach/phoi-do-cung-body-chain\">body chain</a>&nbsp;v&agrave; thắt lưng kim loại &aacute;nh v&agrave;ng đầy vương giả.</p>\r\n\r\n<p><img alt=\"Look 14 cruise 22\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/Dior-cruise-2022-look14.jpeg\" style=\"height:1470px; width:980px\" /></p>\r\n\r\n<p>L&uacute;c lại mạnh mẽ, hiện đại trong những &ldquo;l&aacute;t cắt&rdquo; thể thao với &aacute;o hoodie oversized v&agrave; quần thể thao c&oacute; hoạ tiết nổi bật v&agrave; m&agrave;u sắc tươi s&aacute;ng. Đi k&egrave;m l&agrave; phụ kiện t&uacute;i trống v&agrave; những chiếc&nbsp;<a href=\"https://www.elle.vn/xu-huong-thoi-trang/xu-huong-kinh-mat-he-2021\">k&iacute;nh m&aacute;t</a>&nbsp;hơi hướng futuristic (phong c&aacute;ch vị lai).&nbsp;</p>\r\n\r\n<p><img alt=\"dior cruise hoodie\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439546/Dior-R22-039.jpeg\" style=\"height:1521px; width:1014px\" /></p>\r\n\r\n<p>Nhưng cũng kh&ocirc;ng thể thiếu những khoảnh khắc &ldquo;Dior&rdquo; đặc trưng đầy thanh lịch với c&aacute;c bản phối monochrome gồm blazer chiết eo, quần &acirc;u cạp cao v&agrave;&nbsp;<a href=\"http://elle.vn/xu-huong-thoi-trang/thoi-thuong-cung-vay-xep-ly\">v&aacute;y xếp ly</a>&nbsp;nữ t&iacute;nh.&nbsp;</p>\r\n\r\n<p><img alt=\"Look 15\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-cruise-22-look15.jpeg\" style=\"height:1470px; width:980px\" /></p>\r\n\r\n<p>Điểm nhấn quyền lực, đầy mạnh mẽ v&agrave; c&aacute; t&iacute;nh cho những bộ trang phục Dior Cruise 2022 l&agrave; phụ kiện v&ograve;ng tay v&agrave; harness da đinh t&aacute;n đầy nổi loạn, ph&oacute;ng kho&aacute;ng.&nbsp;</p>\r\n\r\n<p><img alt=\"look 61 với harness\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439546/Dior-R22-061.jpeg\" style=\"height:1566px; width:1044px\" /></p>\r\n\r\n<p><img src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/Dior-cruise-look3-490x735.jpeg\" /></p>\r\n\r\n<p><br />\r\nLook 3</p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 3\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/Dior-cruise-look3-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 6\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-cruise-22-look6-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 9\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look9-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 18\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look18-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 26\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look26-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 28\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look28-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 29\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look29-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 31\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look31-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 32\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look32-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 36\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look36-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 47\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look47-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 51\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look51-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 60\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look60-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 64\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look64-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 66\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look66-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 89\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look89-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 91\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/20/439546/dior-look91-490x735.jpeg\" /></p>\r\n', 'Minh Văn', '2021-06-23 18:54:40', '2021-06-23 19:01:39'),
(7, 'CHUYẾN DU HÀNH VIỄN TƯỞNG ĐẾN VŨ TRỤ THỜI TRANG LOUIS VUITTON CRUISE 2022', 'bst-louis-vuitton-cruise-2022', 2, 'LV-cruise-2022-thumb-445x250.jpg', '<p>Với hơi thở nghệ thuật đậm dấu ấn phong c&aacute;ch futuristic, Nicolas Ghesqui&egrave;re đ&atilde; mang đến giới mộ điệu những ph&uacute;t gi&acirc;y &ldquo;m&atilde;n nh&atilde;n&rdquo; từ s&agrave;n diễn Louis Vuitton Cruise 2022 với những thiết kế<', 0, 1, '<p>Khi mọi hoạt động ngo&agrave;i trời đều bị hạn chế đến mức tối đa sau hơn một năm chống chọi với đại dịch, BST Cruise 2022 của Nicolas Ghesqui&egrave;re v&agrave; Louis Vuitton đ&atilde; truyền nguồn cảm hứng cho sự trở lại rực rỡ của ng&agrave;nh c&ocirc;ng nghiệp thời trang thời k&igrave; hậu Covid. Nếu như BST Cruise 2021 được Gi&aacute;m đốc S&aacute;ng tạo Nicolas Ghesqui&egrave;re tr&igrave;nh diễn ngay tại trụ sở của nh&agrave; mốt nước Ph&aacute;p với h&igrave;nh ảnh những d&atilde;y h&agrave;nh lang trắng v&agrave; b&agrave;n giấy l&agrave;m việc c&ocirc;ng sở. Th&igrave; Louis Vuitton Cruise 2022 sẽ đưa c&aacute;c t&iacute;n đồ thời trang cao cấp chu du đến c&ocirc;ng tr&igrave;nh kiến tr&uacute;c nghệ thuật &ldquo;c&oacute; một kh&ocirc;ng hai&rdquo; Axe Majeur tại Cergy &ndash; Pontoise, nước Ph&aacute;p.</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"315\" id=\"793239509\" src=\"https://www.youtube.com/embed/1ly14-97QqA?enablejsapi=1&amp;origin=https%3A%2F%2Fwww.elle.vn\" title=\"YouTube video player\" width=\"560\"></iframe></p>\r\n\r\n<h2>LOUIS VUITTON CRUISE 2022 &ndash; CHIẾC CỔNG DU H&Agrave;NH ĐẾN KH&Ocirc;NG GIAN VIỄN TƯỞNG</h2>\r\n\r\n<p>Axe Majeur được biết đến l&agrave; t&aacute;c phẩm nghệ thuật trải d&agrave;i suốt 3.2km của nh&agrave; đi&ecirc;u khắc người Israel, Dani Karavan. Tại đ&acirc;y, Louis Vuitton đ&atilde; sử dụng chiếc cầu đỏ bắc ngang quần thể c&ugrave;ng với phần khu&ocirc;n vi&ecirc;n gồm 12 chiếc cột đi&ecirc;u khắc mang sắc trắng tương phản l&agrave;m s&agrave;n diễn ch&iacute;nh. Nh&igrave;n từ g&oacute;c quay rộng, h&igrave;nh ảnh bầu trời xanh với c&aacute;c &aacute;ng m&acirc;y trắng tinh kh&ocirc;i phản chiếu tr&ecirc;n đường băng thời trang như mở ra chiếc cổng du h&agrave;nh đến những chiều kh&ocirc;ng gian kh&aacute;c. C&oacute; thể n&oacute;i, ch&iacute;nh những n&eacute;t chấm ph&aacute; s&aacute;ng tạo trong kiến tr&uacute;c nơi đ&acirc;y đ&atilde; th&agrave;nh c&ocirc;ng truyền tải tinh thần si&ecirc;u việt m&agrave;&nbsp;<a href=\"https://www.elle.vn/tin-thoi-trang/nicolas-o-lai-thuong-hieu-louis-vuitton\">Nicolas Ghesqui&egrave;re</a>&nbsp;muốn gửi gắm trong BST Cruise 2022.</p>\r\n\r\n<p><img alt=\"Bói cảnh sàn diễn LV Cruise \" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/lv-cruise2022-setscene.jpeg\" style=\"height:1333px; width:2000px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>ĐẮM M&Igrave;NH V&Agrave;O VŨ TRỤ V&Agrave; THI&Ecirc;N H&Agrave; BAO LA</h2>\r\n\r\n<p>Nicolas Ghesqui&egrave;re, với tr&iacute; tưởng tượng v&agrave; cảm hứng bất tận từ cuộc du h&agrave;nh vũ trụ, đ&atilde; khiến giới mộ điệu phải &ldquo;tr&ograve;n mắt&rdquo; trước những thiết kế đột ph&aacute; đậm chất&nbsp;<a href=\"https://www.elle.vn/tin-thoi-trang/bst-tui-xach-theo-phong-cach-futuristic-cua-ntk-tre-tai-le\">futuristic</a>&nbsp;(phong c&aacute;ch vị lai). Louis Vuitton Cruise 2022 mang đến cho người xem những cảm x&uacute;c cho&aacute;ng ngợp với sự pha trộn t&aacute;o bạo giữa thiết kế, kết cấu chất liệu v&agrave; hoạ tiết h&igrave;nh in.&nbsp;</p>\r\n\r\n<p>Ghesqui&egrave;re đ&atilde; &ldquo;thoả sức&rdquo; thể hiện sự s&aacute;ng tạo của m&igrave;nh tr&ecirc;n c&aacute;c thiết kế mới với&nbsp;<a href=\"https://www.elle.vn/xu-huong-thoi-trang/hoa-tiet-thoi-trang-he-2021\">hoạ tiết</a>&nbsp;in đầy nghệ thuật lồng gh&eacute;p giữa h&igrave;nh ảnh c&aacute;c h&agrave;nh tinh v&agrave; mặt trăng với những điều th&acirc;n thuộc trong cuộc sống như con người chơi thể thao, b&atilde;i đậu xe hay thang cuốn. NTK đ&atilde; v&iacute; đ&acirc;y như cuộc khai ph&aacute; của con người tới những h&agrave;nh tinh tưởng tượng trong thi&ecirc;n h&agrave; bao la: &ldquo;Đ&ocirc;i khi, sự độc đ&aacute;o kh&ocirc;ng phải l&agrave; điều qu&aacute; xa vời với ch&uacute;ng ta&rdquo;.</p>\r\n\r\n<p><img alt=\"Look 8 LV Cruise\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/lv-cruise-2022-look8.jpeg\" style=\"height:1470px; width:980px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><img alt=\"look 7\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/18/439464/LV_CRUISE_22_LOOK_07.jpeg\" style=\"height:1379px; width:919px\" /></h3>\r\n\r\n<p><a href=\"https://www.elle.vn/the-gioi-thoi-trang/bst-louis-vuitton-pre-fall-2021\" rel=\"article_gallery\" title=\"Louis Vuitton Pre-Fall 2021: Vuittamins cho mùa Thu đầy màu sắc\"><img alt=\"Louis Vuitton Pre-Fall 2021: Vuittamins cho mùa Thu đầy màu sắc\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/21/436155/LV-PreFall-21-thumb-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/the-gioi-thoi-trang/bst-louis-vuitton-pre-fall-2021\" title=\"Louis Vuitton Pre-Fall 2021: Vuittamins cho mùa Thu đầy màu sắc\">Louis Vuitton Pre-Fall 2021: Vuittamins cho m&ugrave;a Thu đầy m&agrave;u sắc</a></p>\r\n\r\n<p>M&ugrave;a Thu n&agrave;y, NTK Nicolas Ghesqui&egrave;re c&ugrave;ng Louis Vuitton đem đến cho l&agrave;ng thời trang những trang phục v&agrave; phụ kiện ngập tr&agrave;n m&agrave;u sắc th&ocirc;ng qua BST...</p>\r\n\r\n<p>C&aacute;c thiết kế đậm chất Space Age (Thời đại du h&agrave;nh kh&ocirc;ng gian) đ&atilde; khắc hoạ điểm nh&igrave;n đầy sự h&agrave;o hứng v&agrave; lạc quan từ h&atilde;ng thời trang nước Ph&aacute;p trong BST Cruise 2022.&nbsp;Ti&ecirc;u biểu như những chiếc &aacute;o kho&aacute;c v&agrave; đầm liền thoải m&aacute;i với cấu tr&uacute;c đặc biệt nhằm tạo độ phồng v&agrave; sự sống động cho trang phục. Kh&ocirc;ng thể kh&ocirc;ng nhắc đến việc sử dụng da b&oacute;ng v&agrave; chất liệu bạc &aacute;nh kim bắt mắt để thể hiện r&otilde; n&eacute;t tinh thần khoa học viễn tưởng.&nbsp;</p>\r\n\r\n<p><img alt=\"Áo khoác da đỏ\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/LV-ao-khoac-do.jpeg\" style=\"height:2000px; width:1334px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"váy xanh louis vuitton cruise 2022\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/18/439464/Louis-Vuitton-ra-mat-bo-suu-tap-cruise-2022-theo-phong-cach-vien-tuong-6-1623830668-680-width980height1470.jpeg\" style=\"height:1470px; width:980px\" /></p>\r\n\r\n<h2>CUỘC DIỄU H&Agrave;NH VƯỢT THỜI GIAN NGOẠN MỤC</h2>\r\n\r\n<p>B&ecirc;n cạnh c&aacute;c trang phục phong c&aacute;ch Space Age mới lạ, cảm hứng s&aacute;ng tạo cho BST mới của Nicolas c&ograve;n đến từ những ban diễu h&agrave;nh nh&agrave; binh trang nghi&ecirc;m. Thiết kế với đường cắt may mạnh mẽ, cứng c&aacute;p c&ugrave;ng c&aacute;c chi tiết đặc trưng như cầu vai qu&acirc;n h&agrave;m, n&uacute;t &aacute;o kim loại &aacute;nh v&agrave;ng với hoạ tiết chạm khắc dọc theo th&acirc;n &aacute;o v&agrave; phần cổ tay l&agrave; một trong những điểm nhấn nổi bật của Louis Vuitton Cruise 2022.&nbsp;</p>\r\n\r\n<p><img alt=\"áo quân đội\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/18/439464/LV_CRUISE_22_LOOK_45.jpeg\" style=\"height:1536px; width:1024px\" /></p>\r\n\r\n<p><a href=\"https://www.elle.vn/bo-suu-tap/khi-nghe-thuat-co-dai-xuyen-khong-den-louis-vuitton-thu-dong-2021\" title=\"Khi nghệ thuật cổ đại \"><img alt=\"Khi nghệ thuật cổ đại \" src=\"https://www.elle.vn/wp-content/uploads/2021/03/12/427232/elle-vietnam-louis-vuitton-fall-winter-21-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/bo-suu-tap/khi-nghe-thuat-co-dai-xuyen-khong-den-louis-vuitton-thu-dong-2021\" title=\"Khi nghệ thuật cổ đại \">Khi nghệ thuật cổ đại &quot;xuy&ecirc;n kh&ocirc;ng&quot; đến Louis Vuitton Thu - Đ&ocirc;ng 2021</a></p>\r\n\r\n<p>Hơn cả một cuộc h&agrave;nh tr&igrave;nh, Louis Vuitton bắt đầu cuộc phi&ecirc;u lưu mới với bộ sưu tập Thu - Đ&ocirc;ng 2021 kết hợp những n&eacute;t vẽ tuyệt vời của xưởng chế...</p>\r\n\r\n<p>Tư duy thẩm mỹ v&agrave; &oacute;c s&aacute;ng tạo của Ghesqui&egrave;re kh&ocirc;ng dừng lại ở những thiết kế&nbsp;<a href=\"https://www.elle.vn/tin-thoi-trang/louis-vuitton-monogram-since-1854\">monogram</a>&nbsp;đặc trưng hay c&aacute;ch &ldquo;chơi m&agrave;u&rdquo; độc đ&aacute;o tr&ecirc;n những chiếc t&uacute;i x&aacute;ch. Ở m&ugrave;a Cruise 2022, NTK đ&atilde; &ldquo;thổi l&agrave;n gi&oacute; mới&rdquo; cho những thiết kế đặc trưng của Louis Vuitton. &Ocirc;ng thay thế quai đeo da kinh điển bằng những mắt x&iacute;ch tr&ograve;n bản to &ldquo;ph&oacute;ng đại&rdquo; sặc sỡ đầy nổi bật. Phần th&acirc;n t&uacute;i cũng l&agrave; sự phối hợp sống động của c&aacute;c &ocirc; vu&ocirc;ng m&agrave;u sắc lấy cảm hứng từ nghệ thuật đại ch&uacute;ng&nbsp;<a href=\"https://www.elle.vn/the-gioi-thoi-trang/5-nghe-si-pop-art-co-anh-huong-nhat-den-thoi-trang-hien-dai\">Pop Art</a>.</p>\r\n\r\n<p><img alt=\"túi hoạ tiết pop art LV\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/lv-chain-bag-scaled.jpeg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sẽ l&agrave; một thiếu s&oacute;t nếu ch&uacute;ng ta bỏ lỡ những đ&ocirc;i bốt cao bồi đầy m&agrave;u sắc với phần mũi nhọn như những l&aacute; cờ đu&ocirc;i nheo lu&ocirc;n xuất hiện trong c&aacute;c cuộc diễu h&agrave;nh n&aacute;o nhiệt.&nbsp;</p>\r\n\r\n<p><img alt=\"Bốt cao bồi LV\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/boot-cao-boi-lvcruise22.jpeg\" style=\"height:1536px; width:1024px\" /></p>\r\n\r\n<p>Khi mọi hoạt động ngo&agrave;i trời đều bị hạn chế đến mức tối đa sau hơn một năm chống chọi với đại dịch, BST Cruise 2022 của Nicolas Ghesqui&egrave;re v&agrave; Louis Vuitton đ&atilde; truyền nguồn cảm hứng cho sự trở lại rực rỡ của ng&agrave;nh c&ocirc;ng nghiệp thời trang thời k&igrave; hậu Covid.</p>\r\n\r\n<p><img src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/lv-cruise22-matchingset-490x735.jpeg\" /></p>\r\n\r\n<p><br />\r\nLook 5</p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 2\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/ao-khoac-da-louisvuitton-2022-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 5\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/lv-cruise22-matchingset-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 6\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/ao-khoac-a-line-hoa-tiet-lv22-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 12\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/ao-khoac-louis-vuitton-2022-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 13\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/louis-vuitton-ao-khoac-cruise-2022-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 15\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/vay-da-lv-cruise-2022-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 25\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/ao-khoac-nha-binh-louis-vuitton-2022-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 27\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/ao-khoac-chan-vay-long-lv-22-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 29\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/ao-khoac-bo-binh-lv-22-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 31\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/thiet-ke-anh-bac-lv-22-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 37\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/ao-tay-phong-lv-cruise22-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 40\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/vay-chat-lieu-kim-loai-22-lv-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 41\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/ao-khoac-da-den-lv-22-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 42\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/ao-khoac-hoa-tiet-sac-so-lv-22-490x735.jpeg\" /></p>\r\n\r\n<p><img alt=\"&lt;br/&gt;Look 44\" src=\"https://www.elle.vn/wp-content/uploads/2021/06/19/439464/lv-cruise-22-490x735.jpeg\" /></p>\r\n', 'Minh Văn', '2021-06-23 19:04:15', '2021-06-23 19:04:57'),
(8, '5 ĐIỀU CẦN BIẾT VỀ BST CHANEL CRUISE 2021/22', '5-dieu-ve-bst-chanel-cruise-2021-2022', 2, 'thumbnail_02_Cruise_2021_22_show_Finale_pictures_copyright_CHANEL_2-445x250.jpg', '<p>Mặc d&ugrave; buổi tr&igrave;nh diễn kh&ocirc;ng người xem v&igrave; tu&acirc;n thủ c&aacute;c biện ph&aacute;p an to&agrave;n thời Covid-19, BST Chanel Cruise 2021/22 vẫn đưa kh&aacute;n giả tới một kh&ocirc;ng gian khơi gợi tr&iacute; tưởng tượng v&a', 0, 1, '<p><img alt=\"bộ trang phục thứ 26 trong show thời trang chanel cruise 22\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look26.jpg\" style=\"height:1256px; width:850px\" /></p>\r\n\r\n<p>(Ảnh: Chanel)</p>\r\n\r\n<p>BST Chanel Cruise 2021/22 được tr&igrave;nh diễn tại hang đ&aacute; Carri&egrave;res de Lumi&egrave;res, miền nam nước Ph&aacute;p. Nh&agrave; mốt đ&atilde; ghi h&igrave;nh m&agrave;n tr&igrave;nh diễn của c&aacute;c người mẫu sau đ&oacute; ph&aacute;t tr&ecirc;n c&aacute;c nền tảng kỹ thuật số v&agrave;o ng&agrave;y 4 th&aacute;ng 5 vừa qua.&nbsp;</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"411\" id=\"529807173\" src=\"https://www.youtube.com/embed/ybcwFo3vO4s?feature=oembed&amp;enablejsapi=1&amp;origin=https%3A%2F%2Fwww.elle.vn\" title=\"Cruise 2021/22 Show — CHANEL Shows\" width=\"730\"></iframe></p>\r\n\r\n<h2>CẢM HỨNG TỪ BỘ PHIM CỦA JEAN COCTEAU</h2>\r\n\r\n<p>Bộ phim đen trắng &ldquo;Testament of Orpheus&rdquo; (Di ch&uacute;c của Orpheus) của Jean Cocteau trong khung cảnh tuyệt đẹp với với h&igrave;nh ảnh người đ&agrave;n &ocirc;ng v&agrave; con ngựa đen đi xuống hang động Carri&egrave;res de Lumi&egrave;res, h&igrave;nh b&oacute;ng anh nổi bật tr&ecirc;n những bức tường trắng&hellip; đ&atilde; in đậm trong t&acirc;m tr&iacute; NTK Virginie Viard v&agrave; dẫn lối tới cảm hứng s&aacute;ng tạo BST Chanel Cruise 2021/22. Đen v&agrave; trắng l&agrave; hai m&agrave;u đặc trưng của nh&agrave; Chanel, việc t&igrave;m thấy cảm hứng mới cho hai sắc th&aacute;i n&agrave;y c&agrave;ng trở n&ecirc;n &yacute; nghĩa hơn.</p>\r\n\r\n<p>Phải n&oacute;i th&ecirc;m rằng,&nbsp;Jean Cocteau &ndash; một nh&agrave; thơ, nh&agrave; thiết kế đồ hoạ, vẽ tranh minh hoạ, nh&agrave; viết kịch v&agrave; l&agrave;m phim người Ph&aacute;p, cũng ch&iacute;nh l&agrave; tri kỷ của Coco Chanel. V&igrave; vậy những mối li&ecirc;n hệ đến c&aacute;c BST Chanel được kết nối thật tự nhi&ecirc;n.</p>\r\n\r\n<p><img alt=\"phim trắng đen của đạo diễn Jean Cocteau quay tại hang đá Carrières de Lumières\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/testament-d-orphee-60-tou-2-g_-_atelier_lucien_clergue_saif_2020.jpg\" style=\"height:350px; width:540px\" /></p>\r\n\r\n<p>Phim của Jean Cocteau được quay trong hang Carri&egrave;res de Lumi&egrave;res năm 1959. (Ảnh: GettyImages)</p>\r\n\r\n<h2>S&Agrave;N DIỄN TRONG HANG Đ&Aacute; CARRI&Egrave;RES DE LUMI&Egrave;RES</h2>\r\n\r\n<p>Hang đ&aacute; Carri&egrave;res de Lumi&egrave;res ở Les Baux-de-Provence, miền nam nước Ph&aacute;p c&ograve;n được gọi l&agrave; &lsquo;đ&aacute; của phương nam&rsquo;, đ&aacute; Les Baux l&agrave; một loại đ&aacute; v&ocirc;i thường c&oacute; m&agrave;u trắng hoặc v&agrave;ng.<br />\r\nC&aacute;c khối đ&aacute; v&ocirc;i trắng đặc trưng của khu vực n&agrave;y dễ d&agrave;ng được gia c&ocirc;ng v&agrave; được sử dụng để x&acirc;y dựng Glanum, ng&ocirc;i l&agrave;ng thời trung cổ Les Baux-de-Provence v&agrave; để x&acirc;y dựng L&acirc;u đ&agrave;i Les Baux..&nbsp;</p>\r\n\r\n<p><img alt=\"không gian mỹ thuật hang đá Carrières de Lumières\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/14_Cruise_2021_22_show_decor_pictures_copyright_CHANEL_14_resize.jpg\" style=\"height:667px; width:1000px\" /></p>\r\n\r\n<p>Hang đ&aacute; Carri&egrave;res de Lumi&egrave;res. (Ảnh: Chanel)</p>\r\n\r\n<p>V&agrave;o năm 1959, Jean Cocteau quay phim &ldquo;Testament of Orpheus&rdquo; v&igrave; bị thu h&uacute;t bởi vẻ đẹp của nơi n&agrave;y v&agrave; cảnh quan xung quanh.&nbsp;Năm 2002, thị trấn Baux-de-Provence giao cho Culturespaces quản l&yacute; mỏ đ&aacute;. Từ đ&acirc;y, hang được&nbsp;đặt t&ecirc;n ti&ecirc;ng Ph&aacute;p l&agrave; &lsquo;Les Carri&egrave;res de Lumi&egrave;res&rsquo;. Culturespaces đ&atilde; ph&aacute;t triển một kh&aacute;i niệm độc đ&aacute;o v&agrave; s&aacute;ng tạo cho nơi n&agrave;y, trở th&agrave;nh khu triển l&atilde;m, thưởng thức nghệ thuật. Carri&egrave;res de Lumi&egrave;res mở m&agrave;n v&agrave;o th&aacute;ng 3 năm đ&oacute; với triển l&atilde;m tranh của&nbsp; Paul Gauguin v&agrave; Van Gogh. Cho đến nay, nơi đ&acirc;y thường tổ chức c&aacute;c triển l&atilde;m tranh 3D với việc chiếu tranh của c&aacute;c danh hoạ l&ecirc;n tường đ&aacute;, tạo th&agrave;nh kh&ocirc;ng gian mỹ thuật hiếm c&oacute;.</p>\r\n\r\n<p>Năm nay, Chanel đ&atilde; mang tới Carri&egrave;res de Lumi&egrave;res BST Cruise 2021/22, d&agrave;n người mẫu tr&igrave;nh diễn trong hang với m&agrave;n thả chim bồ c&acirc;u ở cuối show gợi lại cảnh phim &ldquo;Testament of Orpheus&rdquo;.</p>\r\n\r\n<p><img alt=\"Cảnh cuối show thời trang Chanel Cruise 22\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/01_Cruise_2021_22_show_Finale_pictures_copyright_CHANEL_1_resize.jpg\" style=\"height:667px; width:1000px\" /></p>\r\n\r\n<p>Người mẫu thả chim bồ c&acirc;u sau c&aacute;c m&agrave;n catwalk trong hang đ&aacute;. (Ảnh: Chanel)</p>\r\n\r\n<h2>TUA RUA, ĐEN V&Agrave; TRẮNG, HOA OẢI HƯƠNG, VẢI TWEED</h2>\r\n\r\n<p>&ldquo;Bởi v&igrave; sự đơn giản, ch&iacute;nh x&aacute;c v&agrave; chất thơ trong phim của Cocteau, t&ocirc;i muốn tạo ra một bộ sưu tập thật tinh kh&ocirc;i, với hai t&ocirc;ng m&agrave;u rất ri&ecirc;ng biệt, tạo n&ecirc;n từ m&agrave;u trắng s&aacute;ng v&agrave; m&agrave;u đen đậm&hellip; Để t&aacute;i hiện lại sự hiện đại trong phim của Cocteau, t&ocirc;i muốn một thứ g&igrave; đ&oacute; kiểu rock. Tua rua bằng da, hạt cườm v&agrave; sequins, &aacute;o thun in gương mặt của người mẫu Lola Nicon &ndash; một ng&ocirc;i sao nhạc rock, mặc với bộ đồ vải tweed v&agrave; mang gi&agrave;y mary-janes m&agrave;u bạc. Một c&aacute;i nh&igrave;n gợi nhớ sự hiện đại của những năm s&aacute;u mươi giống như của nhạc punk &hellip; &rdquo; Gi&aacute;m đốc s&aacute;ng tạo Virginie Viard n&oacute;i về c&aacute;c thiết kế Chanel Cruise 2021/22.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Cận cảnh trang phục và các chi tiết cài áo chanel cruise 22\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/07_Look_003d04_resize.jpg\" style=\"height:1256px; width:850px\" /></p>\r\n\r\n<p>M&agrave;u trắng, những chi tiết m&agrave;u trắng, tinh kh&ocirc;i như trong tưởng tượng của Virginie Viard. (Ảnh: Chanel)</p>\r\n\r\n<p><img alt=\"Một bộ trang phục trong show thời trang Chanel Cruise 2021 - 2022\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/86_Look_042d01_resize.jpg\" style=\"height:1256px; width:850px\" /></p>\r\n\r\n<p>Những b&ocirc;ng oải hương được th&ecirc;u quanh eo quần m&agrave;u trắng. (Ảnh: Chanel)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hai m&agrave;u đen &ndash; trắng lu&acirc;n phi&ecirc;n xuất hiện xuy&ecirc;n suốt BST mang lại cảm gi&aacute;c tinh kh&ocirc;i như NTK Virginie Viard mong muốn. Chanel Cruise 2021/22 l&agrave; một BST của những c&acirc;u chuyện cảm hứng đan xen, đ&oacute; l&agrave; nhiều cảnh quan thi&ecirc;n nhi&ecirc;n, cảm x&uacute;c phim ảnh, t&igrave;nh bạn gắn kết Gabrielle Chanel v&agrave; Jean Cocteau&hellip; Những b&ocirc;ng hoa oải hương t&iacute;m biếc tr&ecirc;n c&aacute;nh đồng v&ugrave;ng Provence nức tiếng cũng được thấy tr&ecirc;n một số thiết kế, b&ecirc;n cạnh c&acirc;y xạ hương, mao lương xinh xắn, ch&uacute;ng được th&ecirc;u l&ecirc;n vải.</p>\r\n\r\n<p>Trang phục tweed m&ugrave;a n&agrave;y của Chanel được tạo n&ecirc;n theo hướng bền vững bởi những người thợ từ nh&agrave; thủ c&ocirc;ng danh tiếng Lesage.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"trang phục vải tweed và túi xách chanel từ Chanel Cruise 2022 show\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/17_Look_009d02_resize.jpg\" style=\"height:1256px; width:850px\" /></p>\r\n\r\n<p>&Aacute;o kho&aacute;c vải tweed được ph&aacute;t triển bền vững, c&ugrave;ng với t&uacute;i da tua rua. (Ảnh: Chanel)</p>\r\n\r\n<p><img alt=\"trang phục váy in họa tiết và logo chìm Chanel show Chanel 2022\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/102_Look_052d02_resize.jpg\" style=\"height:1256px; width:850px\" /></p>\r\n\r\n<p>(Ảnh: Chanel)</p>\r\n\r\n<p><img alt=\"Hình ảnh cuối show diễn thời trang Chanel Cruise 2022\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/02_Cruise_2021_22_show_Finale_pictures_copyright_CHANEL_2_resize.jpg\" style=\"height:667px; width:1000px\" /></p>\r\n\r\n<p>Những h&igrave;nh ảnh cuối buổi tr&igrave;nh diễn CHANEL Cruise 2021 &ndash; 2022. (Ảnh: Chanel)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>CĂN HỘ CẢM HỨNG HỨNG BẤT TẬN TR&Ecirc;N ĐƯỜNG RUE CAMBON</h2>\r\n\r\n<p>Căn hộ của Gabrielle Coco Chanel tr&ecirc;n đường&nbsp;Rue Cambon, Paris gần đ&acirc;y đ&atilde; được mở lại sau một đợt tr&ugrave;ng tu lớn. NTK Viard t&igrave;m thấy&nbsp;nguồn cảm hứng từ đ&oacute; với những đồ vật, t&aacute;c phẩm nghệ thuật. Một số được phỏng lại để xuất hiện tr&ecirc;n trang phục trong BST n&agrave;y.</p>\r\n\r\n<p>Một đoạn phim v&agrave; ảnh đen trắng về BST cũng được thực hiện trong kh&ocirc;ng gian căn hộ để sử dụng cho th&ocirc;ng c&aacute;o b&aacute;o ch&iacute;. &nbsp;</p>\r\n\r\n<p><img alt=\"chanel cruise 22 phim thời trang trắng đen\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/Chanel-cruise-22.jpg\" style=\"height:667px; width:1000px\" /></p>\r\n\r\n<p>(Ảnh: Chanel)</p>\r\n\r\n<h2>BUỔI TR&Igrave;NH DIỄN &Acirc;M NHẠC&nbsp;</h2>\r\n\r\n<p>Sau khi m&agrave;n tr&igrave;nh diễn kh&eacute;p lại tr&ecirc;n m&agrave;n h&igrave;nh, người y&ecirc;u Chanel được thưởng thức một buổi diễn &acirc;m nhạc của nhạc sĩ v&agrave; đại sứ Chanel &ndash; S&eacute;bastien Tellier c&ugrave;ng c&aacute;c &nbsp;kh&aacute;ch mời đặc biệt l&agrave; Vanessa Paradis, Charlotte Casiraghi, Juliette Armanet v&agrave; Ang&egrave;le. Trong đoạn phim ghi lại h&igrave;nh ảnh biểu diễn v&agrave; &acirc;m thanh trong trẻo được ph&aacute;t liền sau m&agrave;n catwalk, c&aacute;c đại sứ Chanel v&agrave; bạn b&egrave; của nh&agrave; mốt đ&atilde; l&ecirc;n s&acirc;n khấu để c&oacute; những khoảnh khắc giai điệu tuyệt vời trong bầu kh&ocirc;ng kh&iacute; thoải m&aacute;i của ng&ocirc;i l&agrave;ng Les Baux-de-Provence. V&agrave; tất nhi&ecirc;n l&agrave; tất cả đều tu&acirc;n thủ c&aacute;c quy tr&igrave;nh nghi&ecirc;m ngặt ph&ograve;ng chống Covid-19 hiện đang được &aacute;p dụng.</p>\r\n\r\n<p>C&aacute;c ca kh&uacute;c: Sunny, La Dolce Vita, Baby One More Time&hellip; được ng&acirc;n nga dưới &aacute;nh nắng Les Baux-de-Provence, mang lại cảm gi&aacute;c nhẹ nh&agrave;ng, s&acirc;u lắng.</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"411\" id=\"207924746\" src=\"https://www.youtube.com/embed/G5-oaQAKIJA?feature=oembed&amp;enablejsapi=1&amp;origin=https%3A%2F%2Fwww.elle.vn\" title=\"Musical Performance by Sébastien Tellier. Cruise 2021/22 Show — CHANEL Shows\" width=\"730\"></iframe></p>\r\n\r\n<p><img src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look02-490x724.jpg\" /></p>\r\n\r\n<p>(Ảnh: Chanel)</p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look02-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look2-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/ChanelCruise-22-look12-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look13-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look20-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look-23-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look26-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look29-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look30-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look31-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look32-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look35-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look36-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look37-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look38-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look44-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look54-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look55-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcruise-22-look58-490x724.jpg\" /></p>\r\n\r\n<p><img alt=\"(Ảnh: Chanel)\" src=\"https://www.elle.vn/wp-content/uploads/2021/05/06/435006/chanelcrusie-22-look10-490x724.jpg\" /></p>\r\n', 'Minh Văn', '2021-06-23 19:08:22', '0000-00-00 00:00:00'),
(9, 'STELLA MCCARTNEY THU – ĐÔNG 2021: “D IS FOR DESIRE” CHỮA LÀNH THẾ GIỚI HẬU COVID VỚI Ý THỨC XÃ HỘI', 'stella-mccartney-thudong2021', 2, 'stella-mccartney-bst-thu-2021-445x250.jpg', '<p>2021 đ&aacute;nh dấu sự hồi sinh của thương hiệu Stella Mccartney qua nhiều thăng trầm v&agrave; thay đổi cơ cấu, truyền tải tất cả đam m&ecirc; v&agrave; hy vọng v&agrave;o bộ sưu tập.</p>\r\n', 0, 1, '<p>Như một nốt thăng tự nhi&ecirc;n tiếp nối bản tuy&ecirc;n ng&ocirc;n từ A (tr&aacute;ch nhiệm) đến Z (zero waste) trong bộ sưu tập Xu&acirc;n &ndash; H&egrave;, Stella McCartney Thu &ndash; Đ&ocirc;ng 2021&nbsp;tăng nhiệt v&agrave; k&iacute;ch th&iacute;ch con tim của người h&acirc;m mộ với mẫu in g&acirc;y ảo gi&aacute;c, phom đồ thể thao, &aacute;o tunic cho vũ trường,&nbsp;<a href=\"https://www.elle.vn/the-gioi-thoi-trang/phan-biet-cac-kieu-quan-ong-rong-co-ban\">quần ống rộng</a>&nbsp;v&agrave; thiết kế ph&oacute;ng kho&aacute;ng. Những t&ocirc;ng&nbsp;<a href=\"https://www.elle.vn/xu-huong-thoi-trang/phoi-trang-phuc-mau-neon\">m&agrave;u neon</a>&nbsp;đầy sức sống từ v&agrave;ng, xanh aqua, chanh, hồng l&uacute;c h&ograve;a quyện, l&uacute;c va đập tr&ecirc;n nền nhạc techno v&agrave; đ&egrave;n m&agrave;u. Người phụ nữ trong con mắt của nh&agrave; thiết kế xuất hiện tr&agrave;n đầy hy vọng trong khung cảnh xi măng th&ocirc; của bảo t&agrave;ng Tate modern. Lột x&aacute;c từ h&igrave;nh ảnh nh&agrave; hoạt động m&ocirc;i trường nhẹ nh&agrave;ng mơ m&agrave;ng của m&ugrave;a xu&acirc;n, c&ocirc; ấy đ&atilde; sẵn s&agrave;ng cho một cuộc sống mới.</p>\r\n\r\n<p>Kh&ocirc;ng c&ograve;n ngủ qu&ecirc;n trong sự an to&agrave;n của bốn bức tường v&agrave; sự c&ocirc; đơn của c&aacute;ch li x&atilde; hội, c&ocirc; ấy b&ugrave;ng ch&aacute;y với kh&aacute;t khao kh&ocirc;ng chỉ tồn tại m&agrave; được sống hết m&igrave;nh trong một thế giới đau thương v&igrave; dịch bệnh.</p>\r\n\r\n<p><a href=\"https://www.elle.vn/tin-thoi-trang/stella-mccartney-chien-dich-ung-thu-vu-2020\" title=\"Stella McCartney và chiến dịch \"><img alt=\"Stella McCartney và chiến dịch \" src=\"https://www.elle.vn/wp-content/uploads/2020/10/27/420961/stella-mccartney-phong-chong-ung-thu-vu-thumb-ngang-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/tin-thoi-trang/stella-mccartney-chien-dich-ung-thu-vu-2020\" title=\"Stella McCartney và chiến dịch \">Stella McCartney v&agrave; chiến dịch &quot;l&aacute; thư y&ecirc;u thương&quot; hưởng ứng th&aacute;ng nơ hồng</a></p>\r\n\r\n<p>Với chủ đề &quot;A Letter to My Loved Ones&quot;, chiến dịch đ&atilde; truyền tải những th&ocirc;ng điệp cảm động từ tinh thần quyết liệt đấu tranh với ung thư v&uacute; v&agrave;...</p>\r\n\r\n<p><img alt=\"stella mccartney áo cổ lọ vàng phối boots xanh\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/27/431989/stella-mccartney-ao-co-lo-vang-phoi-boots-xanh-scaled.jpg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p><img alt=\"stella mccartney đầm ôm màu hồng\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/27/431989/stella-mccartney-dam-om-hong-scaled.jpg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p>Người mẫu Phương Oanh tr&igrave;nh diễn BST Xu&acirc;n &ndash; H&egrave; 2021 của Stella McCartney.</p>\r\n\r\n<p>2021 đ&aacute;nh dấu sự hồi sinh của thương hiệu Stella Mccartney qua nhiều thăng trầm v&agrave; thay đổi cơ cấu. Stella truyền tải tất cả đam m&ecirc; v&agrave; hi vọng của m&igrave;nh v&agrave;o bộ sưu tập; t&ocirc;n vinh, kết nối v&agrave; điều phối những yếu tố tưởng l&agrave; rời rạc một c&aacute;ch h&agrave;i h&ograve;a như một nghệ sĩ Kintsugi (nghệ thuật gh&eacute;p gốm vỡ nhật bản). Chọn lựa trong thiết kế lu&ocirc;n c&oacute; t&iacute;nh &yacute; thức m&ocirc;i trường v&agrave; x&atilde; hội, 77% (tăng 3% so với năm 2019) của bộ sưu tập được l&agrave;m từ nguy&ecirc;n liệu t&aacute;i sinh kĩ thuật cao như PCV-free sequins, Vegan Alter Mat, ECONYL&reg;. Gi&agrave;y v&agrave; phụ kiện được l&agrave;m từ ABS plug t&aacute;i chế với đế biodegradable (c&oacute; thể ph&acirc;n hủy).</p>\r\n\r\n<p><img alt=\"stella mccartney đầm xanh xẻ\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/27/431989/stella-mccartney-dam-xanh-scaled.jpg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p><img alt=\"stella mccartney sơ mi kẻ sọc phối áo cổ lọ\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/27/431989/stella-mccartney-so-mi-ke-soc-phoi-ao-co-lo-scaled.jpg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p><a href=\"https://www.elle.vn/bo-suu-tap/bst-chanel-thu-dong-2021\" rel=\"article_gallery\" title=\"Chanel Thu - Đông 2021: Mùa Đông của thời trang giữa lòng Paris\"><img alt=\"Chanel Thu - Đông 2021: Mùa Đông của thời trang giữa lòng Paris\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/11/427028/chanel-thu-dong-2021-elle-viet-nam-1-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/bo-suu-tap/bst-chanel-thu-dong-2021\" title=\"Chanel Thu - Đông 2021: Mùa Đông của thời trang giữa lòng Paris\">Chanel Thu - Đ&ocirc;ng 2021: M&ugrave;a Đ&ocirc;ng của thời trang giữa l&ograve;ng Paris</a></p>\r\n\r\n<p>Vẫn l&agrave; một bộ sưu tập thời trang được giới thiệu trong thời kỳ COVID-19, Chanel đ&atilde; chia sẻ một video đầy cảm hứng thời trang v&agrave; văn h&oacute;a Paris: từ...</p>\r\n\r\n<p>Chọn lựa về con người cho show diễn mang t&iacute;nh h&ograve;a nhập cao, t&ocirc;n vinh những m&agrave;u da kh&aacute;c nhau với tỉ lệ người mẫu da trắng v&agrave; da m&agrave;u b&igrave;nh đẳng một c&aacute;ch đ&aacute;ng kh&acirc;m phục. Tinh thần của bộ sưu tập thể hiện dũng kh&iacute; của nh&agrave; thiết kế &ndash; ch&uacute;ng ta c&oacute; thể thấy được sự t&aacute;o bạo v&agrave; sức trẻ từ nguồn gốc londoner club kid trong Stella cũng như sự bất khuất của một nh&agrave; thiết kế d&aacute;m đấu tranh v&agrave; h&agrave;nh động để bảo vệ tr&aacute;i đất m&agrave; m&igrave;nh y&ecirc;u qu&yacute;.</p>\r\n\r\n<p>Thường được đ&aacute;nh gi&aacute; l&agrave; một thương hiệu ph&aacute; c&aacute;ch hay ngược d&ograve;ng, Stella nổi loạn bằng c&aacute;ch t&ocirc;n vinh &yacute; thức&nbsp; x&atilde; hội, cộng đồng v&agrave;&nbsp;<a href=\"https://www.elle.vn/the-gioi-thoi-trang/thoi-trang-ben-vung-thay-doi-thap-nien-2020\">bảo vệ m&ocirc;i trường</a>&nbsp;với tất cả con tim để x&acirc;y dựng những gi&aacute; trị trường tồn hơn thời trang hay cảm x&uacute;c tho&aacute;ng qua một m&ugrave;a.</p>\r\n\r\n<p><img alt=\"stella mccartney áo blazer họa tiết \" src=\"https://www.elle.vn/wp-content/uploads/2021/03/27/431989/stella-mccartney-blazer-hoa-tiet-scaled.jpg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p><img alt=\"stella mccartney bộ jumpsuit kẻ sọc\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/27/431989/stella-mccartney-jumpsuit-ke-soc-scaled.jpg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p><img alt=\"stella mccartney quần ống loe\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/27/431989/stella-mccartney-quan-ong-rong-scaled.jpg\" style=\"height:2560px; width:1707px\" /></p>\r\n\r\n<p><img alt=\"stella mccartney suit quần ống loe\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/27/431989/stella-mccartney-suit-quan-ong-rong.jpg\" style=\"height:2100px; width:1400px\" /></p>\r\n\r\n<p><img alt=\"stella mccartney áo cut out phối quần legging\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/27/431989/stella-mccartney-ao-cat-xe-phoi-quan-legging-scaled.jpg\" style=\"height:2560px; width:1707px\" /></p>\r\n', 'Minh Văn', '2021-06-23 19:10:59', '0000-00-00 00:00:00');
INSERT INTO `daovanminh_post` (`postId`, `postTitle`, `alias`, `topicId`, `image`, `summary`, `trash`, `status`, `detail`, `createBy`, `createDate`, `updateDate`) VALUES
(10, 'KHI NGHỆ THUẬT CỔ ĐẠI “XUYÊN KHÔNG” ĐẾN LOUIS VUITTON THU – ĐÔNG 2021', 'khi-nghe-thuat-co-dai-xuyen-khong-den-louis-vuitton-thu-dong-2021', 1, 'elle-vietnam-louis-vuitton-fall-winter-21-445x250.jpg', '<p>Hơn cả một cuộc h&agrave;nh tr&igrave;nh, Louis Vuitton bắt đầu cuộc phi&ecirc;u lưu mới với bộ sưu tập Thu &ndash; Đ&ocirc;ng 2021 kết hợp những n&eacute;t vẽ tuyệt vời của xưởng chế t&aacute;c nghệ thuật l&acirc;u đời &ndash; Fornasetti.</p>\r\n', 0, 1, '<p>Gi&aacute;m đốc s&aacute;ng tạo Ghesqui&egrave;re đưa đường băng của Louis Vuitton Thu &ndash; Đ&ocirc;ng 2021 v&agrave;o Denon Wing, trong bảo t&agrave;ng Louvre. C&aacute;c người mẫu tr&igrave;nh diễn thiết kế mới b&ecirc;n c&aacute;c t&aacute;c phẩm đi&ecirc;u khắc La M&atilde;, Hy Lạp v&agrave; Etruscan cổ đại. Tr&ecirc;n nền nhạc &ldquo;Around the World&rdquo; của Daft Punk &ndash; bộ đ&ocirc;i nhạc điện tử người Ph&aacute;p, buổi tr&igrave;nh diễn kh&ocirc;ng kh&aacute;n giả đ&atilde; được ghi h&igrave;nh v&agrave; ph&aacute;t tr&ecirc;n c&aacute;c nền tảng trực tuyến theo lịch tr&igrave;nh Tuần lễ thời trang Paris.</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"411\" id=\"130280203\" src=\"https://www.youtube.com/embed/FVBIcPETT8I?feature=oembed&amp;enablejsapi=1&amp;origin=https%3A%2F%2Fwww.elle.vn\" title=\"Women’s Fall-Winter 2021 Fashion Show | LOUIS VUITTON\" width=\"730\"></iframe></p>\r\n\r\n<p><img alt=\"không gian trình diễn louis vuitton thu - đông 2021\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/khong-gian-trinh-dien-louis-vuitton-thu-dong-2021.jpg\" style=\"height:1000px; width:1500px\" /></p>\r\n\r\n<p>Một phần kh&ocirc;ng gian của bảo t&agrave;ng Louvre trở th&agrave;nh nơi Louis Vuitton tr&igrave;nh diễn BST Thu &ndash; Đ&ocirc;ng 2021.</p>\r\n\r\n<p><a href=\"https://www.elle.vn/tin-thoi-trang/louis-vuitton-monogram-since-1854\" rel=\"noopener\" target=\"_blank\">Louis Vuitton</a>&nbsp;mượn những kiệt t&aacute;c qu&aacute; khứ để thể hiện tương lai. Ch&uacute;ng ta đều c&oacute; thể nh&igrave;n thấy điều đặc biệt ấy từ c&aacute;c BST thời trang của nh&agrave; mốt Ph&aacute;p nhưng m&ugrave;a Thu &ndash; Đ&ocirc;ng 2021 n&agrave;y điều đ&oacute; được thể hiện một c&aacute;ch nổi bật. Cộng t&aacute;c với Fornasetti, một xưởng thiết kế nghệ thuật c&oacute; lịch sử h&igrave;nh th&agrave;nh từ năm 1940 tại nước &Yacute;, gi&aacute;m đốc s&aacute;ng tạo Nicolas Ghesqui&egrave;re đ&atilde; kh&aacute;m ph&aacute; kho lưu trữ phong ph&uacute; với 13.000 bức tranh l&agrave;m cảm hứng cho bộ sưu tập.</p>\r\n\r\n<p><a href=\"https://www.elle.vn/bo-suu-tap/bst-louis-vuitton-xuan-he-2020\" rel=\"article_gallery\" title=\"BST Louis Vuitton Xuân - Hè 2020: Dòng chảy thời gian và nghệ thuật\"><img alt=\"BST Louis Vuitton Xuân - Hè 2020: Dòng chảy thời gian và nghệ thuật\" src=\"https://www.elle.vn/wp-content/uploads/2019/10/cac-thiet-ke-trong-bo-suu-tap-louis-vuitton-xuan-he-2020-2-445x250.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://www.elle.vn/bo-suu-tap/bst-louis-vuitton-xuan-he-2020\" title=\"BST Louis Vuitton Xuân - Hè 2020: Dòng chảy thời gian và nghệ thuật\">BST Louis Vuitton Xu&acirc;n - H&egrave; 2020: D&ograve;ng chảy thời gian v&agrave; nghệ thuật</a></p>\r\n\r\n<p>Thời trang trong thập kỷ thứ 2 của thế kỷ 21 sẽ thay đổi ra sao? BST Louis Vuitton Xu&acirc;n - H&egrave; 2020 ch&iacute;nh l&agrave; c&acirc;u trả lời của Nicolas Ghesqui&egrave;re.</p>\r\n\r\n<p>Với h&agrave;ng ng&agrave;n bức tranh từ xưởng nghệ thuật Fornasetti, những khu&ocirc;n mặt vẽ tay nổi tiếng về phụ nữ từ thời cổ đại đ&atilde; xuất hiện tr&ecirc;n quần &aacute;o, đồ da, phụ kiện thời trang của Louis Vuitton Thu &ndash; Đ&ocirc;ng năm nay. C&aacute;c chất liệu được kết hợp một c&aacute;ch đa diện, vải jacquard, nylon v&agrave; nhiều hơn thế. Những chiếc &aacute;o kho&aacute;c được tạo d&aacute;ng như c&aacute;c t&aacute;c phẩm đi&ecirc;u khắc. Ch&uacute;ng c&oacute; h&igrave;nh d&aacute;ng qu&aacute; khổ v&agrave; được kết hợp bay bổng với ch&acirc;n v&aacute;y, quần mang cảm hứng si&ecirc;u thực. Loạt t&uacute;i x&aacute;ch đặc sắc với nhiều bản t&uacute;i k&iacute;ch cỡ kh&aacute;c nhau, thiết kế đa dạng với cảm hứng từ xưởng nghệ thuật Fornasetti l&agrave; chủ đạo.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/LouisVuittonFW21elle.jpg\" style=\"height:1500px; width:1000px\" /></p>\r\n\r\n<p>Hoạ tiết tr&ecirc;n &aacute;o kho&aacute;c Louis Vuitton sử dụng c&aacute;c bản vẽ từ xưởng nghệ thuật Fornasetti.</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/louis-vuitton-1.jpg\" style=\"height:742px; width:1000px\" /></p>\r\n\r\n<p>Những chiếc t&uacute;i Louis Vuitton Thu &ndash; Đ&ocirc;ng 2021.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mọi thứ được thể hiện rất thuần t&uacute;y trong thời Hy Lạp, La M&atilde; cổ đại, một nền thẩm mỹ m&agrave; t&iacute;nh ưu việt kh&oacute; c&oacute; thể thể kiểm chứng. Ch&iacute;nh v&igrave; thế, nguồn cảm hứng l&agrave; bất tận v&agrave; rộng mở với mọi bộ &oacute;c s&aacute;ng tạo. Những n&eacute;t khắc tinh xảo, huyền ảo của một thời đại bền bỉ được Louis Vuitton đưa v&agrave;o &aacute;o quần, phụ kiện một c&aacute;ch th&uacute; vị.&nbsp;</p>\r\n\r\n<h2>&mdash;</h2>\r\n\r\n<h3><em>NTK NICOLAS GHESQUI&Egrave;RE N&Oacute;I VỀ QU&Aacute; TR&Igrave;NH S&Aacute;NG TẠO BST VỚI GIỌNG ĐIỆU ĐẦY TRIẾT L&Yacute;: &ldquo;KH&Aacute;M PH&Aacute; C&Aacute;C KHO LƯU TRỮ CỦA FORNASETTI C&Oacute; CẢM GI&Aacute;C PHẤN KH&Iacute;CH NHƯ MỘT CUỘC KHAI QUẬT KHẢO CỔ HỌC, T&Igrave;M KIẾM C&Aacute;C BẢN VẼ TỪ QU&Aacute; KHỨ ĐỂ MANG LẠI CHO CH&Uacute;NG MỘT CUỘC SỐNG MỚI VỚI LOUIS VUITTON &mdash; CHO HIỆN TẠI V&Agrave; TƯƠNG LAI.&rdquo;</em></h3>\r\n\r\n<h2>&mdash;</h2>\r\n\r\n<p><img alt=\"bst louis vuitton thu đông 2021 1\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/bst-louis-vuitton-thu-dong-2021-1.jpg\" style=\"height:1500px; width:1000px\" /><img alt=\"bst louis vuitton thu đông 2021 2\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/bst-louis-vuitton-thu-dong-2021-2.jpg\" style=\"height:1500px; width:1000px\" /><img alt=\"bst louis vuitton thu đông 2021 3\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/bst-louis-vuitton-thu-dong-2021-3.jpg\" style=\"height:1500px; width:1000px\" /><img alt=\"bst louis vuitton thu đông 2021 4\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/bst-louis-vuitton-thu-dong-2021-4.jpg\" style=\"height:1500px; width:1000px\" /><img alt=\"bst louis vuitton thu đông 2021 5\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/bst-louis-vuitton-thu-dong-2021-5.jpg\" style=\"height:1500px; width:1000px\" /><img alt=\"bst louis vuitton thu đông 2021 6\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/bst-louis-vuitton-thu-dong-2021-6.jpg\" style=\"height:1500px; width:1000px\" /><img alt=\"bst louis vuitton thu đông 2021 7\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/bst-louis-vuitton-thu-dong-2021-7.jpg\" style=\"height:1500px; width:1000px\" /><img alt=\"bst louis vuitton thu đông 2021 8\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/bst-louis-vuitton-thu-dong-2021-8.jpg\" style=\"height:1500px; width:1000px\" /><img alt=\"bst louis vuitton thu đông 2021 9\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/bst-louis-vuitton-thu-dong-2021-9.jpg\" style=\"height:1500px; width:1000px\" /><img alt=\"bst louis vuitton thu đông 2021 10\" src=\"https://www.elle.vn/wp-content/uploads/2021/03/15/427232/bst-louis-vuitton-thu-dong-2021-10.jpg\" style=\"height:1500px; width:1000px\" /></p>\r\n', 'Minh Văn', '2021-06-23 19:13:02', '0000-00-00 00:00:00');

-- --------------------------------------------------------

--
-- Cấu trúc bảng cho bảng `daovanminh_product`
--

CREATE TABLE `daovanminh_product` (
  `productid` int(11) UNSIGNED NOT NULL,
  `productName` varchar(100) NOT NULL,
  `alias` varchar(100) NOT NULL,
  `catId` int(11) UNSIGNED NOT NULL,
  `collectionId` int(11) UNSIGNED NOT NULL,
  `detail` text NOT NULL,
  `price` float NOT NULL,
  `saleprice` float DEFAULT NULL,
  `image` varchar(100) NOT NULL,
  `status` tinyint(1) NOT NULL,
  `trash` tinyint(1) NOT NULL DEFAULT 0
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Đang đổ dữ liệu cho bảng `daovanminh_product`
--

INSERT INTO `daovanminh_product` (`productid`, `productName`, `alias`, `catId`, `collectionId`, `detail`, `price`, `saleprice`, `image`, `status`, `trash`) VALUES
(1, 'Áo sơ mi xám cổ trụ', 'ao-so-mi-xam-co-tru', 1, 1, '- Chất Vải :\r\n\r\nVải polyester mềm mịn, ít nhăn, ít bám bụi giữ dáng không bai gião cho dù giặt nhiều lần.\r\n\r\nChất vải này rất bền chắc giúp người mặc có thể thoải mái vận động mà không lo vải bị xù lông hay bung chỉ.\r\n\r\n- Kiểu dáng :\r\n\r\nDáng áo sơ mi ôm nhẹ cơ thể, đặc biệt là cổ áo và dáng lưng áo ôm vừa phải giúp tôn dáng người mặc trông trẻ trung nam tính hơn.\r\n\r\nCổ tay áo có ống bo gọn và được máy chắc giúp người mặc thoải mái vận động di chuyển trong các hoạt động mà không lo bị vướng.\r\n\r\n- Màu sắc :\r\n\r\nXám Tiêu\r\n\r\n- Bảo quản :\r\n\r\nÁo sơ mi được làm bằng chất vải polyester ít thấm nước nên anh em có thể ngâm nước một chút trước khi giặt để áo giặt sạch sẽ hơn.', 25.5, 23, 'Minh_img07.jpg', 1, 0),
(2, 'Áo sơ mi trắng tay dài', 'ao-so-mi-trang-tay-dai', 1, 1, '• Thành phần vải : 100%nano.\r\n• Kiểu dáng : trẻ trung , thanh lịch.\r\n• Thiết kế cổ bẻ, tay dài\r\n• Cài nút dọc thân, 1 túi đắp ngực\r\n• Form Regular fit tôn dáng\r\n• Đường may chắc chắn, tỉ mỉ\r\n• Tính năng kháng khuẩn tự nhiên ưu việt.\r\n• Mềm mịn, thoáng khí.\r\n• Chống tia cực tím , khử mùi và bền màu.\r\n• Xuất xứ : Việt Nam\r\n• Size :38-43.', 30, 25, 'Minh_img06.jpg', 1, 0),
(3, 'Áo sơ mi xanh nhạt tay dài', 'ao-so-mi-xanh-nhat-tay-dai', 1, 1, 'Chất liệu : Vải nến lụa , mềm mịn , không nhăn , không xù , không bay màu .\r\n\r\nFrom : Ôm dáng body.\r\n\r\nĐường may : Chắc chắn và tỉ mỉ.\r\nSize M: 45-54 kg . cao 1,58m – 1,63m . tay áo: 60cm.\r\n\r\nSize L: 55kg – 61kg . cao 1,60 m -1,65m . tay áo: 61cm.\r\n\r\nSize XL: 62kg – 65kg . cao 1,66m – 1,68m . tay áo: 62cm.\r\n\r\nSize 2XL: 66kg - 73kg . cao 1,68m - 172,m . tay áo: 63cm\r\n\r\nSize 3XL: 74kg đến 88kg. cao 1,73m – 1,78m . tay áo: 64cm.\r\n\r\n*Lưu ý : Mẫu shop là from ôm dáng , nếu bạn muốn mặc rộng thoải mái nêm chọn lên 1 zize.', 27, 25, 'Minh_img08.jpg', 1, 0),
(4, 'Áo ba lỗ thời trang', 'ao-ba-lo-thoi-trang', 1, 1, '·Chất liệu thun mềm mại co giãn tốt , thoáng mát\r\n\r\n·Thiết kế thời trang phù hợp xu hướng hiện nay\r\n\r\n·Kiểu dáng đa phong cách\r\n\r\n·Đường may tinh tế sắc sảo\r\n\r\n·Áo thun được thiết kế vể đẹp trẻ trung năng động nhưng không kém phần mạnh mẽ.\r\n\r\n·Áo được thiết kế đẹp, chuẩn form, đường may sắc xảo, vải cotton dày, mịn, thấm hút mồ hôi tạo sự thoải mái khi mặc!\r\n\r\n·Dễ dàng phối trang phục , thích hợp đi chơi đi làm đi dạo phố\r\n\r\n·Thích hợp cho sự kết hợp vứi quần jean, sọt,kaki!', 15, 12, 'Minh_img21.jpg', 1, 0),
(5, 'Áo thun giả hai áo tay caro', 'ao-thun-gia-hai-ao-tay-caro', 1, 1, 'Chất liệu: : LEN THUN\r\nChất vải thoáng mát tạo cảm giác thoải mái\r\nThiết kế mang phong cách hiện đại, cá tính\r\nKiểu dáng : thời trang đẹp mắt\r\nSize: free size ( phù hợp cho các bạn từ chiều cao 150cm -165cm ; cân nặng:50-70kg)', 18, 16, 'Minh_img22.jpg', 1, 0),
(6, 'Áo sơ mi trắng tay ngắn sọc dọc', 'ao-so-mi-trang-tay-ngan-soc-doc', 1, 1, 'Áo sơ mi nam ngắn tay sọc dọc màu tím giúp nam giới toát lên vẻ lịch lãm đầy nam tính, chất liệu sợi tre mềm mại, thoáng mát, chống nhăn suốt ngày dài năng động. Màu sắc nhẹ nhàng dễ phối với mọi loại trang phục cho mọi phong cách, cổ áo và tay áo phối nhẹ tạo điểm nhấn giúp bạn trông thật khác biệt.\r\n\r\nChất liệu: 50% Bamboo, 50% Microfiber\r\nMàu sắc: VIOLET\r\nForm dáng: Slimfit lai ngang', 23, 22.5, 'Minh_img05.jpg', 1, 0),
(7, 'Áo kiểu nữ xẻ trụ', 'ao-kieu-nu-xe-tru', 2, 2, 'Chất liệu: 100% cotton.\r\n\r\nĐặc tính: Mềm mại, hút ẩm tốt, thấm hút tốt.\r\n\r\nHướng dẫn sử dụng:\r\n\r\n- Giặt ở nhiệt độ bình thường.\r\n\r\n- Không được dùng hóa chất tẩy, ủi ở nhiệt độ thích hợp.\r\n\r\n- Hạn chế sử dụng máy sấy', 15, 14, 'Minh_img01.jpg', 1, 0),
(8, 'Áo kiểu nữ tay lỡ', 'ao-kieu-nu-tay-lo', 2, 2, 'Kiểu dáng trẻ trung, năng động\r\nChất liệu vải mềm mại, màu sắc tươi sáng\r\nĐường may kĩ càng, sắc sảo\r\n- Kiểu dáng: Áo cổ tròn, tay lỡ\r\n- Màu: trắng, hồng\r\n- Size: freesize\r\n- Chất liệu: tơ gân', 15, 13.75, 'Minh_img02.jpg', 1, 0),
(9, 'Áo kiểu nữ cổ xếp xinh xắn', 'ao-kieu-nu-co-xep-xinh-xan', 2, 2, 'Xuất Xứ: Việt Nam\r\nChất Liệu: Chifon cát\r\nMàu Sắc: Xám\r\nSize: S, M, L, XL\r\nKích Thước:\r\n- Size S: Ngực 84, eo 66, mông 88 (cm)\r\n- Size M: Ngực 88, eo 70, mông 92 (cm)\r\n- Size L: Ngực 92, eo 75, mông 96 (cm)\r\n- Size XL: Ngực 96, eo 79, mông 100 (cm)\r\n\r\nĐặc điểm sản phẩm:\r\n- Thiết kế hiện đại trẻ trung, phong cách Hàn Quốc\r\n\r\n- Kiểu dáng thời trang, mang đậm nét dịu dàng, nữ tính\r\n\r\n- Dáng suông dễ mặc, phù hợp nhiều đối tượng khách hàng\r\n\r\n- Màu sắc cơ bản, giúp nàng thoải mái mix-mach nhiều phong cách\r\n\r\n- Phù hợp nhiều hoàn cảnh, dễ mix cùng quần tây, quần kiểu, chân váy', 18, 16.7, 'Minh_img03.jpg', 1, 0),
(10, 'Áo Croptop Cổ Lọ Ngắn Tay Body', 'ao-croptop-co-lo-ngan-tay-body', 2, 2, 'Áo Croptop cổ lọ ngắn tay ôm body\r\n- chất thun lụa mềm, mịn, mát (vải không dày nhưng không quá mỏng)\r\n- freesize vòng 1 dưới 90\r\n- Màu: trắng\r\n- Dài áo 39cm', 12.54, 9.24, 'Minh_img04.jpg', 1, 0),
(11, 'Quần lửng nam họa tiết', 'quan-lung-nam-hoa-tiet', 3, 3, '- Xuất xứ: Việt Nam\r\n- Chất liệu: 100% Cotton.\r\n- Màu sắc: xám phối trắng\r\n- Size: 29/30/31/32/33/34/35/36', 10, 8.79, 'Minh_img23.jpg', 1, 0),
(12, 'quần short bermuda hình chai', 'quan-short-bermuda-hinh-chai', 3, 3, 'Chất liệu: 78% polyester, 17% rayon, 5% spandex\r\nForm: Regular\r\nĐặc tính:\r\n- Thiết kế túi đắp năng động và tiện ích, thoải mái tuyệt đối khi vận động.\r\n- Thoáng mát, co giãn, thấm hút mồ hôi tốt.\r\nHướng dẫn sử dụng:\r\n- Giặt ở nhiệt độ bình thường.\r\n- Không được dùng hóa chất tẩy, ủi ở nhiệt độ thích hợp.\r\n- Hạn chế sử dụng máy sấy.', 11, NULL, 'Minh_img24.jpg', 1, 0),
(13, 'Quần tây nam xanh', 'quan-tay-nam-xanh', 3, 3, 'Size: 28, 30, 32, 34, 36, 38, 40\r\nLoại: Skinny Fit, Regular Fit, Slim Fit Các loại\r\nvải: Linen, Polyester Wool', 26.9, 20.17, 'Minh_img25.jpg', 1, 0),
(14, 'Quần jogger họa tiết hoạt hình', 'quan-jogger-hoa-tiet-hoat-hinh', 3, 3, 'Chất liệu:  DÙ\r\nhọa tiết không cố định\r\nChất vải thoáng mát tạo cảm giác thoải mái\r\nThiết kế mang phong cách hiện đại, cá tính\r\nKiểu dáng : thời trang đẹp mắt\r\nSize: free size ( phù hợp cho các bạn từ chiều cao 150cm -165cm ; cân nặng:50-70kg)', 12, NULL, 'Minh_img26.jpg', 1, 0),
(15, 'Quần ống rộng nữ mặc công sở', 'quan-ong-rong-nu', 4, 4, 'Giới tính: Phụ nữ\r\nLoại mô hình: Rắn\r\nPhong cách phía trước: Phẳng\r\nLoại đóng cửa: Thắt lưng đàn hồi\r\nLoại vải: Vải rộng\r\nLoại eo: Giữa\r\nPhong cách: Phong cách Anh\r\nChất liệu: Polyester, Cotton, Spandex\r\nTrang trí: Không có\r\nKiểu quần : Quần ống rộng\r\nSố mô hình: 3621\r\nLoại phù hợp: Lỏng lẻo\r\nChiều dài: Quần dài đến mắt cá chân\r\nPhong cách: quần ống rộng\r\nSuit: quần phụ nữ\r\nKích thước: cộng với kích thước 6XL\r\nChiều dài: quần dài đến mắt cá chân\r\nKiểu: quần công sở, quần nữ\r\nEo: quần cạp cao', 40.66, 35.37, 'Minh_img27.jpg', 1, 0),
(16, 'Quần tây ống đứng', 'quan-tay-ong-dung', 4, 4, '1. VẢI TUYẾT MƯA CAO CẤP, TRÔNG RẤT SANG, PHOM ĐẸP NHƯ HÀNG MAY ĐO.\r\n2. THIẾT KẾ THỜI THƯỢNG ĐIỆU ĐÀ NỮ TÍNH MÀ THƯỚT THA MỀM MẠI, ỐNG SUÔNG RỘNG SANG VÀ CHẢNH.\r\n3. MÓN ĐỒ KHÔNG THỂ THIẾU TRONG TỦ ĐỒ CỦA NHỮNG CÔ NÀNG CÔNG SỞ CÓ GU ĂN MẶC TRONG NĂM NAY.\r\n4. MIX VỚI CÁC LOẠI ÁO TỪ SƠ MI ĐẾN ÁO THUN ÁO PHÔNG, ĐỀU HỢP, ĐẶC BIỆT ỐNG QUẦN THIẾT KẾ RỘNG SUÔNG, VÀ DÀI, CHỊ EM NÊN MIX VỚI DÀY CAO GÓT DÁNG SẼ THẬT MĨ MIỀU MỀM MẠI VỚI CÁC CÔ NÀNG CAO THÌ KO SỢ NGẮN MÀ CÁC CHỊ EM CHIỀU CAO KHIÊM TỐN MANG GIÀY CAO GÓT THÌ ĂN GIAN ĐƯỢC CHIỀU CAO KINH NGẠC!!!', 19, 18.28, 'Minh_img28.jpg', 1, 0),
(17, 'Quần ngắn giả váy', 'quan-ngan-gia-vay', 4, 4, 'Màu sắc: Trắng\r\nChất liệu: Khaki pha\r\nĐặc tính: Co giãn nhẹ, đứng form, ít nhăn\r\nLưu ý giặt ủi: Giặt tay, ủi mặt trái ở nhiệt độ thấp', 7.8, NULL, 'Minh_img29.jpg', 1, 0),
(18, 'Quần tây nữ ống đứng thời trang', 'quan-tay-nu-ong-dung', 4, 4, 'Xuất Xứ: Việt Nam\r\nChất Liệu: Kaki hàn\r\nMàu Sắc: Đen\r\nSize: S/M/L/XL\r\n\r\nĐặc điểm sản phẩm\r\n- Thiết kế đơn giản phù hợp nhiều đối tượng\r\n\r\n- Trang phục cần nên có trong tủ đồ của phái đẹp\r\n\r\n- Màu sắc cơ bản cho nàng thoải mái trong khâu mix mach\r\n\r\n- Chất liệu có độ co giãn nhẹ đem lại cảm giác dễ chịu thoải mái\r\n\r\n- Có thể kết hợp cùng các loại áo khác nhau như áo sơ mi, áo kiểu, áo thun...', 21, NULL, 'Minh_img30.jpg', 1, 0),
(19, 'Wilke Rodriguez đen', 'wilke-rodriguez-black', 1, 1, 'Được thiết kế với màu đen linh hoạt, bộ đồ này có áo khoác hai cúc và quần xẻ tà phía trước để tạo vẻ ngoài hiện đại.\r\n85% len, 15% polyester\r\nÁo khoác hai cúc\r\nVe áo Notch\r\nLỗ thông hơi bên\r\nQuần lọt khe phẳng phía trước có đường viền chưa hoàn thiện để bạn có thể may theo số đo chính xác của mình.\r\nLót đầu gối\r\nPhù hợp hiện đại\r\nĐã nhập khẩu\r\nChỉ được giặt khô', 59.98, 40.19, 'Minh_img09.jpg', 1, 0),
(20, 'Bộ đồ công sở nam có ngực đôi màu xanh hải quân', 'bo-do-cong-so-nam-co-nguc-doi', 1, 1, 'Loại phía trước quần: Mặt trước phẳng	Kích thước vòng eo: 30 in-46 in\r\nGiới tính: Đàn ông\r\nThủ công: Đúng\r\nMùa: Thu, Xuân, Hạ, Đông\r\nChiều dài áo khoác / áo khoác:	Đều đặn\r\nMẫu: Chất rắn\r\nCắt áo khoác: Phần Ngực\r\nPhù hợp: Đều đặn\r\nPhong cách: Áo đuôi tôm\r\nSố lượng các mảnh: 2 hột \r\nKích thước vòng ngực: 36-52\r\nKiểu: Bộ đồ\r\nVật chất: Chât bông tổng hợp\r\nMàu sắc: Màu xanh da trời\r\nChủ đề:	Thập niên 80, 90, Mỹ, Đám cưới\r\nCổ điển: Không\r\nLoại phía trước: Mặt trước phẳng\r\nMPN: Không áp dụng\r\nPhong cách ve áo khoác:	Cao điểm\r\nKiểu chân: Thẳng\r\nTăng lên: Giữa\r\nDịp: Kinh doanh, Thông thường, Trang trọng, Tiệc / Cocktail, Du lịch, Đám cưới\r\nChăm sóc hàng may mặc:	Chỉ được giặt khô\r\nInseam:	Kích thước tiêu chuẩn hoặc tùy chỉnh\r\nVật liệu lót: Polyester\r\nKiểu nút phía trước áo khoác: Sáu nút\r\nPhong cách thông hơi áo khoác: Hai lỗ thông hơi\r\nChiều dài áo khoác:Đều đặn\r\nLoại kích thước: Đều đặn', 89, 84.55, 'Minh_img10.jpg', 1, 0),
(21, 'Bộ đồ vừa vặn hiện đại', 'bo-do-vua-van-hien-dai', 1, 1, 'Suit tách biệt cho phép bạn chọn kích cỡ áo khoác, áo vest và quần riêng biệt để có một sự vừa vặn lý tưởng. Chiếc áo khoác mỏng vừa vặn này được làm thủ công từ vải Ý tốt ở Mỹ. Ghép nối nó với quần lót phù hợp của nó (bán riêng) để có một sự vừa vặn lý tưởng. Khả năng chọn kích cỡ áo khoác và quần một cách độc lập sẽ đảm bảo tính linh hoạt và khả năng mặc tối ưu.\r\n\r\nÁo khoác:\r\nPhù hợp hiện đại. Hai nút. Đã lót đầy đủ. Ve áo có khía. Lỗ thông hơi bên\r\n\r\nQuần:\r\nPhù hợp hiện đại. Mặt trước phẳng. Lót đến đầu gối. Viền chưa hoàn thành để điều chỉnh cho phù hợp với số đo của bạn\r\n\r\nVest:\r\nPhù hợp hiện đại. Nút năm. Đã lót đầy đủ. Túi có dây. Satin trở lại', 419.99, 400, 'Minh_img11.jpg', 0, 0),
(22, 'Bộ đồ sọc đen xám calvin klein x-fit', 'bo-do-soc-den-xam', 1, 1, 'Bộ đồ cực mỏng vừa vặn theo xu hướng này có họa tiết sọc đen và xám tinh tế cùng áo khoác hai bên ngực. Quần lót phẳng phía trước hoàn thiện vẻ ngoài.\r\nVải\r\nNgực đôi\r\nVe áo đỉnh\r\nLỗ thông hơi bên\r\nQuần lót phẳng phía trước\r\nLót đầu gối\r\nKhông được thiết kế để điều chỉnh cho phù hợp với số đo của bạn\r\nCực mỏng phù hợp\r\nĐã nhập khẩu\r\nChỉ được giặt khô', 56.99, NULL, 'Minh_img12.jpg', 1, 0),
(23, 'Suit chú rể', 'suit-chu-re', 1, 1, 'Phù hợp Loại: Thẳng\r\nPhong cách: Chính Thức\r\nLoại sản phẩm:3 cái (Áo & Pant & Vest)\r\nLoại quần đóng cửa:Dây kéo Fly\r\nLoại đóng cửa:Đơn ngực\r\nChất liệu:Len, Tơ nhân tạo, COTTON, Linen\r\nSuit nam : suits nam cho đám cưới \r\nMen suit mỏng vừa vặn:chú rể suit\r\nBộ đồ nam : bộ đồ cưới nam', 67, 52.05, 'Minh_img13.jpg', 1, 0),
(24, 'Suit vừa vặn hiện đại màu xanh nhạt', 'suit-vua-van-hien-dai-mau-xanh-nhat', 1, 1, 'Có được sự phù hợp tùy chỉnh mà bạn đang tìm kiếm với chiếc áo khoác tách biệt bằng len tốt này của JOE Joseph Abboud. Suit tách rời cho phép bạn chọn quần, áo vest và / hoặc áo khoác riêng cho phù hợp với dáng người bạn muốn.\r\n\r\nÁo khoác:\r\nPhù hợp hiện đại. Hai nút. Ve áo có khía. Lỗ thông hơi bên. Lót đầy đủ\r\n\r\nQuần:\r\nPhù hợp hiện đại. Mặt trước phẳng hoặc mặt trước xếp nếp. Lót đến đầu gối. Viền chưa hoàn thành để điều chỉnh cho phù hợp với số đo của bạn', 54.99, NULL, 'Minh_img14.jpg', 1, 0),
(25, 'Áo vest thời trang cho nữ', 'ao-vest-thoi-trang-cho-nu', 2, 2, 'THÔNG TIN SẢN PHẨM\r\n- Tên sản phẩm: Áo vest thời trang cho nữ chất liệu Tuyết mưa BLY AK4\r\n- Thương hiệu: BlyFashion\r\n- Chất liệu: Tuyết mưa\r\n- Màu sắc: Xanh\r\n- Kiểu cổ áo: Cổ vest\r\n- Ống tay: Tay dài\r\n- Cân nặng đóng gói: 100g\r\n- Kích thước sau đóng gói: 25x10x3 cm\r\n\r\nƯU ĐIỂM CỦA SẢN PHẨM\r\n- Sản phẩm được thiết kế sang trọng, giúp các nàng công sở trở nên cuốn hút hơn, thanh lịch hơn.\r\n- Sản phẩm có độ bền màu, khi giặt không bị nhão và có thể giặt máy.\r\n- Sản phẩm có độ giữ Form cao, giúp che khuyết điểm và tôn lên vẻ đẹp của các nàng.\r\n- Vải có độ co giãn cao, nên khi mặc các nàng sẽ có thể thoải mái vận động.\r\n- Chất liệu vải không quá dày, cũng không quá mỏng giúp cho các nàng có thể mặc vào tất cả các mùa trong năm.\r\n- Các sợi vải có thiết kế nằm ngang, đổ xuống giúp vải không bị nhăn và bám bụi khi mặc\r\n- Chất vải thông thoáng, thấm hút và nhả nước nhanh (Khô nhanh).', 17.29, NULL, 'Minh_img15.jpg', 1, 0),
(26, 'Vest nữ phong cách mới', 'vest-nu-phong-cach-moi', 2, 2, 'Tuổi: 25-29 tuổi\r\nVải: Dacron\r\nMô hình: màu rắn\r\nCổ áo: cổ áo\r\nQuần áo bay: một nút\r\nNội dung thành phần: 91% (với) -95% ()\r\nMùa năm: 2020 mùa xuân\r\nChiều dài tay áo: Dài tay\r\nTổng chiều dài: Chung\r\nLoại quần áo: Vừa vặn\r\nChất liệu: Sợi Polyester 94,9% Sợi đàn hồi Polyurethane (sợi đàn hồi polyurethane) 5,1%', 22.74, 19.37, 'Minh_img16.jpg', 1, 0),
(27, 'Đồng phục nữ phong cách', 'dong-phuc-nu-phong-cach', 2, 2, 'Chất liệu: Polyester, MICROFIBER, spandex\r\nTrang trí: Nút\r\nSleeve Length (cm): Đầy đủ\r\nQuần áo dài: REGULAR\r\nVáy dài: Đầu gối trên, Thống\r\nLoại đóng cửa: Nút duy nhất\r\ncổ áo: Hình chữ V\r\nThành phần nguyên liệu: Blazer Fiber\r\nPhong cách: Phụ Nữ văn phòng\r\nGiới tính: WOMEN\r\nLoại sản phẩm: Trang phục váy', 53.91, 51.21, 'Minh_img17.jpg', 1, 0),
(28, 'Bộ vest Nữ', 'bo-vest-nu', 2, 2, 'Chất liệu ngoại nhập\r\nHàng ngoại\r\nThiết kế tay dài  thanh lịch\r\nDáng áo cài 1 nút, vai đệm mút mỏng\r\nSố đo sản phẩm mẫu mặc:Vai đệm: 36 - Ngực: 84 - Eo: 69- Dài áo: 59 - Dài tay: 36 - Rộng tay: 13 (cm)\r\nSize mẫu mặc: M. Số đo người mẫu: 80 - 60 - 88 - 167 (cm)', 44, NULL, 'Minh_img18.jpg', 1, 0),
(29, 'Vest doanh nhân nữ', 'vest-doanh-nhan-nu', 2, 2, 'Thành phần nguyên liệu: polyester\r\nThành phần nguyên liệu: Polyester.\r\nMàu: Xám, Đen\r\nKích thước: S, M, L, XL, XXL, 3XL, 4XL; (Kích thước tham khảo) S: Ngực 32,3 inch (82 cm), Trọng lượng tham chiếu 13,8 - 94,9 lbs (39 - 44 kg), tương đương XS Nhật Bản M: Ngực 35,4 - 10,9 lbs (86 cm), Trọng lượng tham chiếu 10,9 - 12,6 lbs ( 4 lbs (53 kg), Tương đương với cỡ L của Nhật Bản 2 lbs (53 kg). Vòng ngực 98 cm Trọng lượng tham khảo: 123,3 - 132,3 lbs (56 - 60 kg), Tương đương với Cỡ XL của Nhật, Vòng ngực 3XL 40,2 inch (102 cm), Trọng lượng tham khảo: 132,2 - 143,3 lbs (60 - 65 kg), tương đương với 2XL của Nhật Bản\r\nĐây là một bộ trên và dưới. Đơn giản và dễ dàng để trộn và kết hợp! Bộ quần áo này có họa tiết kẻ sọc trưởng thành tạo vẻ ngoài chỉn chu và trưởng thành. Kiểu dáng thanh lịch tôn lên tối đa nét quyến rũ của người phụ nữ trưởng thành.\r\nNó có thể được sử dụng như một chiếc áo khoác tuyển dụng cho các cuộc phỏng vấn tìm việc hoặc như một phong cách đi làm, kinh doanh hoặc văn phòng hàng ngày. Có thể được mặc trong những dịp khác nhau như những dịp bình thường và trang trọng.', 58.95, NULL, 'Minh_img19.jpg', 1, 0),
(30, 'Bộ blazer nữ thời trang', 'bo-blazer-nu-thoi-trang', 2, 2, 'Chất liệu: Polyester\r\nThành phần nguyên liệu: Blazer+Microfiber\r\nQuần áo dài: REGULAR\r\ngiới tính: WOMEN\r\ncổ áo: Hình chữ V\r\nLoại quần đóng cửa: Nút Fly\r\nLoại đóng cửa: Đơn ngực\r\nSleeve Length (cm): Đầy đủ\r\nLoại sản phẩm: Trang phục quần\r\nPhong cách: Phụ Nữ văn phòng\r\nColor: Red\r\nSeason: Spring, Autumn, Winter\r\nSize: S M L XL XXL XXXL 4XL\r\nColor Style: Natural Color\r\nFabric Type: Broadcloth', 58.99, NULL, 'Minh_img20.jpg', 1, 0),
(33, 'sản phẩm cũ', 'sn-phm-moi', 1, 1, '<p>SHEIN l&agrave; một nền tảng thương mại điện tử thời trang nhanh B2C quốc tế. C&ocirc;ng ty chủ yếu tập trung v&agrave;o trang phục của phụ nữ, nhưng cũng cung cấp quần &aacute;o nam, quần &aacute;o trẻ em, phụ kiện, gi&agrave;y d&eacute;p, t&uacute;i x&aacute;ch v&agrave; c&aacute;c mặt h&agrave;ng thời trang kh&aacute;c. SHEIN chủ yếu nhắm v&agrave;o Ch&acirc;u &Acirc;u, Ch&acirc;u Mỹ, &Uacute;c v&agrave; Trung Đ&ocirc;ng c&ugrave;ng với c&aacute;c thị trường ti&ecirc;u d&ugrave;ng kh&aacute;c. Thương hiệu được th&agrave;nh lập v&agrave;o th&aacute;ng 10 năm 2008, duy tr&igrave; quan điểm &quot;mọi người đều c&oacute; thể tận hưởng vẻ đẹp của thời trang&quot;. Hoạt động kinh doanh bao gồm hơn 220 quốc gia v&agrave; khu vực tr&ecirc;n to&agrave;n thế giới.</p>\r\n\r\n<p><img src=\"https://img.shein.com/images2/2018/11/29/15434606131933594897.jpg\" /></p>\r\n', 12, 11, 'banner_10.jpg', 0, 0),
(35, 'quần què', 'quan-quen', 1, 1, 'chi tiet', 15, 10, 'face6.jpg', 0, 0),
(36, 'á', 'a', 1, 1, 'a', 12, 2, 'thumb_image1.jpg', 0, 1);

